	 ('1208','Diprophylline','Diprophylline','000000000','P_Diprophylline',NULL,NULL,NULL,NULL,NULL,NULL,'2',NULL,NULL,NULL,'O','BATCH','2026-02-10 11:27:38.187946','BATCH','2026-02-10 11:27:38.187946'),
	 ('1209','Pyrilamine','Pyrilamine','000000000','P_Pyrilamine',NULL,NULL,NULL,NULL,NULL,NULL,'2',NULL,NULL,NULL,'O','BATCH','2026-02-10 11:27:38.187946','BATCH','2026-02-10 11:27:38.187946'),
