mapper.select:
/* 대국민포털_권한기본 조회 (AuthrtMapper.getAuthrt) */ SELECT authrt_cd ,up_authrt_cd ,task_se_cd ,authrt_nm ,authrt_type_cd ,authrt_expln ,use_yn ,wrtr_dept_nm ,mdfr_dept_nm ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_AUTHRT WHERE 1=1 AND authrt_cd = #{authrtCd}

mapper.insert:
/* 대국민포털_권한기본 입력 (AuthrtMapper.insertAuthrt) */
INSERT INTO kids_own.TB_PP_M_AUTHRT
(
     authrt_cd
    ,up_authrt_cd
    ,task_se_cd
    ,authrt_nm
    ,authrt_type_cd
    ,authrt_expln
    ,use_yn
    ,wrtr_dept_nm
    ,mdfr_dept_nm
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{authrtCd}
    ,#{upAuthrtCd}
    ,#{taskSeCd}
    ,#{authrtNm}
    ,#{authrtType}
    ,#{authrtExpln}
    ,#{useYn}
    ,#{wrtrDeptNm}
    ,#{mdfrDeptNm}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_권한기본 저장 (AuthrtMapper.insertAuthrt) */
INSERT INTO kids_own.TB_PP_M_AUTHRT
(
     authrt_cd
    ,up_authrt_cd
    ,task_se_cd
    ,authrt_nm
    ,authrt_type_cd
    ,authrt_expln
    ,use_yn
    ,wrtr_dept_nm
    ,mdfr_dept_nm
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{authrtCd}
    ,#{upAuthrtCd}
    ,#{taskSeCd}
    ,#{authrtNm}
    ,#{authrtType}
    ,#{authrtExpln}
    ,#{useYn}
    ,#{wrtrDeptNm}
    ,#{mdfrDeptNm}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(authrt_cd)
DO UPDATE SET
     up_authrt_cd = coalesce(excluded.up_authrt_cd, kids_own.TB_PP_M_AUTHRT.up_authrt_cd)
    ,task_se_cd = coalesce(excluded.task_se_cd, kids_own.TB_PP_M_AUTHRT.task_se_cd)
    ,authrt_nm = coalesce(excluded.authrt_nm, kids_own.TB_PP_M_AUTHRT.authrt_nm)
    ,authrt_type_cd = coalesce(excluded.authrt_type_cd, kids_own.TB_PP_M_AUTHRT.authrt_type_cd)
    ,authrt_expln = coalesce(excluded.authrt_expln, kids_own.TB_PP_M_AUTHRT.authrt_expln)
    ,use_yn = coalesce(excluded.use_yn, kids_own.TB_PP_M_AUTHRT.use_yn)
    ,wrtr_dept_nm = coalesce(excluded.wrtr_dept_nm, kids_own.TB_PP_M_AUTHRT.wrtr_dept_nm)
    ,mdfr_dept_nm = coalesce(excluded.mdfr_dept_nm, kids_own.TB_PP_M_AUTHRT.mdfr_dept_nm)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_AUTHRT.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_권한기본 삭제 (AuthrtMapper.deleteAuthrt) */
DELETE FROM kids_own.TB_PP_M_AUTHRT
WHERE 1=1
AND authrt_cd = #{authrtCd}
