mapper.insert:
/* 대국민포털_권한변경이력기본 입력 (AuthrtChgHstryMapper.insertAuthrtChgHstry) */
INSERT INTO "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY"
(
     authrt_chg_sn
    ,authrt_cd
    ,menu_sn
    ,aplcn_trgt_type
    ,chg_role_list
    ,aplcn_trgt
    ,chg_type
    ,rmrk
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{authrtChgSn}
    ,#{authrtCd}
    ,#{menuSn}
    ,#{aplcnTrgtType}
    ,#{chgRoleList}
    ,#{aplcnTrgt}
    ,#{chgType}
    ,#{rmrk}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_권한변경이력기본 저장 (AuthrtChgHstryMapper.insertAuthrtChgHstry) */
INSERT INTO "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY"
(
     authrt_chg_sn
    ,authrt_cd
    ,menu_sn
    ,aplcn_trgt_type
    ,chg_role_list
    ,aplcn_trgt
    ,chg_type
    ,rmrk
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{authrtChgSn}
    ,#{authrtCd}
    ,#{menuSn}
    ,#{aplcnTrgtType}
    ,#{chgRoleList}
    ,#{aplcnTrgt}
    ,#{chgType}
    ,#{rmrk}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(authrt_chg_sn)
DO UPDATE SET
     authrt_cd = coalesce(excluded.authrt_cd, "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY".authrt_cd)
    ,menu_sn = coalesce(excluded.menu_sn, "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY".menu_sn)
    ,aplcn_trgt_type = coalesce(excluded.aplcn_trgt_type, "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY".aplcn_trgt_type)
    ,chg_role_list = coalesce(excluded.chg_role_list, "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY".chg_role_list)
    ,aplcn_trgt = coalesce(excluded.aplcn_trgt, "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY".aplcn_trgt)
    ,chg_type = coalesce(excluded.chg_type, "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY".chg_type)
    ,rmrk = coalesce(excluded.rmrk, "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY".rmrk)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_권한변경이력기본 삭제 (AuthrtChgHstryMapper.deleteAuthrtChgHstry) */
DELETE FROM "KIDS_OWN"."TB_PP_M_AUTHRT_CHG_HSTRY"
WHERE 1=1
AND authrt_chg_sn = #{authrtChgSn}
