mapper.select:
/* 대국민포털_권한메뉴기본 조회 (AuthrtMenuMapper.getAuthrtMenu) */ SELECT authrt_cd ,menu_sn ,authrt_menu_role_rmrk ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_AUTHRT_MENU WHERE 1=1 AND authrt_cd = #{authrtCd} AND menu_sn = #{menuSn}

mapper.insert:
/* 대국민포털_권한메뉴기본 입력 (AuthrtMenuMapper.insertAuthrtMenu) */
INSERT INTO kids_own.TB_PP_M_AUTHRT_MENU
(
     authrt_cd
    ,menu_sn
    ,authrt_menu_role_rmrk
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{authrtCd}
    ,#{menuSn}
    ,#{authrtMenuRoleRmrk}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

mapper.update:
/* 대국민포털_권한메뉴기본 수정 (AuthrtMenuMapper.updateAuthrtMenu) */
UPDATE kids_own.TB_PP_M_AUTHRT_MENU
SET
     authrt_menu_role_rmrk = coalesce(#{authrtMenuRoleRmrk}, authrt_menu_role_rmrk)
    ,mdfr_id = coalesce(#{mdfrId}, mdfr_id)
    ,mdfcn_dt = now()

WHERE 1=1
AND authrt_cd = #{authrtCd}
AND menu_sn = #{menuSn}

mapper.insert:
/* 대국민포털_권한메뉴기본 저장 (AuthrtMenuMapper.insertAuthrtMenu) */
INSERT INTO kids_own.TB_PP_M_AUTHRT_MENU
(
     authrt_cd
    ,menu_sn
    ,authrt_menu_role_rmrk
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{authrtCd}
    ,#{menuSn}
    ,#{authrtMenuRoleRmrk}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(authrt_cd,menu_sn)
DO UPDATE SET
     authrt_menu_role_rmrk = coalesce(excluded.authrt_menu_role_rmrk, kids_own.TB_PP_M_AUTHRT_MENU.authrt_menu_role_rmrk)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_AUTHRT_MENU.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_권한메뉴기본 삭제 (AuthrtMenuMapper.deleteAuthrtMenu) */
DELETE FROM kids_own.TB_PP_M_AUTHRT_MENU
WHERE 1=1
AND authrt_cd = #{authrtCd}
AND menu_sn = #{menuSn}
