mapper.select:
/* 대국민포털_권한메뉴롤기본 조회 (AuthrtMenuRoleMapper.getAuthrtMenuRole) */ SELECT authrt_cd ,role_cd ,menu_sn ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_AUTHRT_MENU_ROLE WHERE 1=1 AND authrt_cd = #{authrtCd} AND role_cd = #{roleCd} AND menu_sn = #{menuSn}

mapper.insert:
/* 대국민포털_권한메뉴롤기본 입력 (AuthrtMenuRoleMapper.insertAuthrtMenuRole) */
INSERT INTO kids_own.TB_PP_M_AUTHRT_MENU_ROLE
(
     authrt_cd
    ,role_cd
    ,menu_sn
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{authrtCd}
    ,#{roleCd}
    ,#{menuSn}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

mapper.update:
/* 대국민포털_권한메뉴롤기본 수정 (AuthrtMenuRoleMapper.updateAuthrtMenuRole) */
UPDATE kids_own.TB_PP_M_AUTHRT_MENU_ROLE
SET
     mdfr_id = coalesce(#{mdfrId}, mdfr_id)
    ,mdfcn_dt = now()

WHERE 1=1
AND authrt_cd = #{authrtCd}
AND role_cd = #{roleCd}
AND menu_sn = #{menuSn}

mapper.insert:
/* 대국민포털_권한메뉴롤기본 저장 (AuthrtMenuRoleMapper.insertAuthrtMenuRole) */
INSERT INTO kids_own.TB_PP_M_AUTHRT_MENU_ROLE
(
     authrt_cd
    ,role_cd
    ,menu_sn
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{authrtCd}
    ,#{roleCd}
    ,#{menuSn}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(authrt_cd,role_cd,menu_sn)
DO UPDATE SET
     mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_AUTHRT_MENU_ROLE.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_권한메뉴롤기본 삭제 (AuthrtMenuRoleMapper.deleteAuthrtMenuRole) */
DELETE FROM kids_own.TB_PP_M_AUTHRT_MENU_ROLE
WHERE 1=1
AND authrt_cd = #{authrtCd}
AND role_cd = #{roleCd}
AND menu_sn = #{menuSn}
