mapper.select:
/* 대국민포털_게시판권한기본 조회 (BbsAuthrtMapper.getBbsAuthrt) */ SELECT bbs_id ,authrt_cd ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_BBS_AUTHRT WHERE 1=1 AND bbs_id = #{bbsId} AND authrt_cd = #{authrtCd}

mapper.insert:
/* 대국민포털_게시판권한기본 입력 (BbsAuthrtMapper.insertBbsAuthrt) */
INSERT INTO kids_own.TB_PP_M_BBS_AUTHRT
(
     bbs_id
    ,authrt_cd
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{bbsId}
    ,#{authrtCd}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

mapper.update:
/* 대국민포털_게시판권한기본 수정 (BbsAuthrtMapper.updateBbsAuthrt) */
UPDATE kids_own.TB_PP_M_BBS_AUTHRT
SET
     mdfr_id = coalesce(#{mdfrId}, mdfr_id)
    ,mdfcn_dt = now()

WHERE 1=1
AND bbs_id = #{bbsId}
AND authrt_cd = #{authrtCd}

mapper.insert:
/* 대국민포털_게시판권한기본 저장 (BbsAuthrtMapper.insertBbsAuthrt) */
INSERT INTO kids_own.TB_PP_M_BBS_AUTHRT
(
     bbs_id
    ,authrt_cd
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{bbsId}
    ,#{authrtCd}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(bbs_id,authrt_cd)
DO UPDATE SET
     mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_BBS_AUTHRT.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_게시판권한기본 삭제 (BbsAuthrtMapper.deleteBbsAuthrt) */
DELETE FROM kids_own.TB_PP_M_BBS_AUTHRT
WHERE 1=1
AND bbs_id = #{bbsId}
AND authrt_cd = #{authrtCd}
