mapper.insert:
/* 대국민포털_부서권한기본 입력 (DeptAuthrtMapper.insertDeptAuthrt) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DEPT_AUTHRT"
(
     dept_no
    ,authrt_cd
    ,dept_authrt_rmrk
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{deptNo}
    ,#{authrtCd}
    ,#{deptAuthrtRmrk}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

mapper.update:
/* 대국민포털_부서권한기본 수정 (DeptAuthrtMapper.updateDeptAuthrt) */
UPDATE "KIDS_OWN"."TB_PP_M_DEPT_AUTHRT"
SET
     dept_authrt_rmrk = coalesce(#{deptAuthrtRmrk}, dept_authrt_rmrk)
    ,mdfr_id = coalesce(#{mdfrId}, mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(#{mdfcnPrgrmId}, mdfcn_prgrm_id)
WHERE 1=1
AND dept_no = #{deptNo}
AND authrt_cd = #{authrtCd}

mapper.insert:
/* 대국민포털_부서권한기본 저장 (DeptAuthrtMapper.insertDeptAuthrt) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DEPT_AUTHRT"
(
     dept_no
    ,authrt_cd
    ,dept_authrt_rmrk
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{deptNo}
    ,#{authrtCd}
    ,#{deptAuthrtRmrk}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(dept_no,authrt_cd)
DO UPDATE SET
     dept_authrt_rmrk = coalesce(excluded.dept_authrt_rmrk, "KIDS_OWN"."TB_PP_M_DEPT_AUTHRT".dept_authrt_rmrk)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_DEPT_AUTHRT".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_DEPT_AUTHRT".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_부서권한기본 삭제 (DeptAuthrtMapper.deleteDeptAuthrt) */
DELETE FROM "KIDS_OWN"."TB_PP_M_DEPT_AUTHRT"
WHERE 1=1
AND dept_no = #{deptNo}
AND authrt_cd = #{authrtCd}
