mapper.insert:
/* 대국민포털_전문가권한기본 입력 (ExprtAuthrtMapper.insertExprtAuthrt) */
INSERT INTO "KIDS_OWN"."TB_PP_M_EXPRT_AUTHRT"
(
     mbr_no
    ,authrt_cd
    ,use_yn
    ,exprt_authrt_rmrk
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{mbrNo}
    ,#{authrtCd}
    ,#{useYn}
    ,#{exprtAuthrtRmrk}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_전문가권한기본 저장 (ExprtAuthrtMapper.insertExprtAuthrt) */
INSERT INTO "KIDS_OWN"."TB_PP_M_EXPRT_AUTHRT"
(
     mbr_no
    ,authrt_cd
    ,use_yn
    ,exprt_authrt_rmrk
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{mbrNo}
    ,#{authrtCd}
    ,#{useYn}
    ,#{exprtAuthrtRmrk}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(mbr_no,authrt_cd)
DO UPDATE SET
     use_yn = coalesce(excluded.use_yn, "KIDS_OWN"."TB_PP_M_EXPRT_AUTHRT".use_yn)
    ,exprt_authrt_rmrk = coalesce(excluded.exprt_authrt_rmrk, "KIDS_OWN"."TB_PP_M_EXPRT_AUTHRT".exprt_authrt_rmrk)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_EXPRT_AUTHRT".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_EXPRT_AUTHRT".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_전문가권한기본 삭제 (ExprtAuthrtMapper.deleteExprtAuthrt) */
DELETE FROM "KIDS_OWN"."TB_PP_M_EXPRT_AUTHRT"
WHERE 1=1
AND mbr_no = #{mbrNo}
AND authrt_cd = #{authrtCd}
