mapper.select:
/* 대국민포털_전문가권한기본 조회 (ExprtAuthrtMapper.getExprtAuthrt) */ SELECT mbr_no ,authrt_cd ,use_yn ,exprt_authrt_rmrk ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_EXPRT_AUTHRT WHERE 1=1 AND mbr_no = #{mbrNo} AND authrt_cd = #{authrtCd}

mapper.insert:
/* 대국민포털_전문가권한기본 입력 (ExprtAuthrtMapper.insertExprtAuthrt) */
INSERT INTO kids_own.TB_PP_M_EXPRT_AUTHRT
(
     mbr_no
    ,authrt_cd
    ,use_yn
    ,exprt_authrt_rmrk
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{mbrNo}
    ,#{authrtCd}
    ,#{useYn}
    ,#{exprtAuthrtRmrk}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

mapper.update:
/* 대국민포털_전문가권한기본 수정 (ExprtAuthrtMapper.updateExprtAuthrt) */
UPDATE kids_own.TB_PP_M_EXPRT_AUTHRT
SET
     use_yn = coalesce(#{useYn}, use_yn)
    ,exprt_authrt_rmrk = coalesce(#{exprtAuthrtRmrk}, exprt_authrt_rmrk)
    ,mdfr_id = coalesce(#{mdfrId}, mdfr_id)
    ,mdfcn_dt = now()

WHERE 1=1
AND mbr_no = #{mbrNo}
AND authrt_cd = #{authrtCd}

mapper.insert:
/* 대국민포털_전문가권한기본 저장 (ExprtAuthrtMapper.insertExprtAuthrt) */
INSERT INTO kids_own.TB_PP_M_EXPRT_AUTHRT
(
     mbr_no
    ,authrt_cd
    ,use_yn
    ,exprt_authrt_rmrk
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{mbrNo}
    ,#{authrtCd}
    ,#{useYn}
    ,#{exprtAuthrtRmrk}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(mbr_no,authrt_cd)
DO UPDATE SET
     use_yn = coalesce(excluded.use_yn, kids_own.TB_PP_M_EXPRT_AUTHRT.use_yn)
    ,exprt_authrt_rmrk = coalesce(excluded.exprt_authrt_rmrk, kids_own.TB_PP_M_EXPRT_AUTHRT.exprt_authrt_rmrk)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_EXPRT_AUTHRT.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_전문가권한기본 삭제 (ExprtAuthrtMapper.deleteExprtAuthrt) */
DELETE FROM kids_own.TB_PP_M_EXPRT_AUTHRT
WHERE 1=1
AND mbr_no = #{mbrNo}
AND authrt_cd = #{authrtCd}
