mapper.insert:
/* 대국민포털_담당자권한기본 입력 (PicAuthrtMapper.insertPicAuthrt) */
INSERT INTO "KIDS_OWN"."TB_PP_M_PIC_AUTHRT"
(
     emp_no
    ,authrt_cd
    ,pic_authrt_rmrk
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{empNo}
    ,#{authrtCd}
    ,#{picAuthrtRmrk}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

mapper.update:
/* 대국민포털_담당자권한기본 수정 (PicAuthrtMapper.updatePicAuthrt) */
UPDATE "KIDS_OWN"."TB_PP_M_PIC_AUTHRT"
SET
     pic_authrt_rmrk = coalesce(#{picAuthrtRmrk}, pic_authrt_rmrk)
    ,mdfr_id = coalesce(#{mdfrId}, mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(#{mdfcnPrgrmId}, mdfcn_prgrm_id)
WHERE 1=1
AND emp_no = #{empNo}
AND authrt_cd = #{authrtCd}

mapper.insert:
/* 대국민포털_담당자권한기본 저장 (PicAuthrtMapper.insertPicAuthrt) */
INSERT INTO "KIDS_OWN"."TB_PP_M_PIC_AUTHRT"
(
     emp_no
    ,authrt_cd
    ,pic_authrt_rmrk
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{empNo}
    ,#{authrtCd}
    ,#{picAuthrtRmrk}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(emp_no,authrt_cd)
DO UPDATE SET
     pic_authrt_rmrk = coalesce(excluded.pic_authrt_rmrk, "KIDS_OWN"."TB_PP_M_PIC_AUTHRT".pic_authrt_rmrk)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_PIC_AUTHRT".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_PIC_AUTHRT".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_담당자권한기본 삭제 (PicAuthrtMapper.deletePicAuthrt) */
DELETE FROM "KIDS_OWN"."TB_PP_M_PIC_AUTHRT"
WHERE 1=1
AND emp_no = #{empNo}
AND authrt_cd = #{authrtCd}
