mapper.insert:
/* 대국민포털_역할기본 입력 (RoleMapper.insertRole) */
INSERT INTO "KIDS_OWN"."TB_PP_M_ROLE"
(
     role_cd
    ,role_nm
    ,role_type
    ,role_expln_cn
    ,use_yn
    ,wrtr_dept_nm
    ,mdfr_dept_nm
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{roleCd}
    ,#{roleNm}
    ,#{roleType}
    ,#{roleExplnCn}
    ,#{useYn}
    ,#{wrtrDeptNm}
    ,#{mdfrDeptNm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_역할기본 저장 (RoleMapper.insertRole) */
INSERT INTO "KIDS_OWN"."TB_PP_M_ROLE"
(
     role_cd
    ,role_nm
    ,role_type
    ,role_expln_cn
    ,use_yn
    ,wrtr_dept_nm
    ,mdfr_dept_nm
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{roleCd}
    ,#{roleNm}
    ,#{roleType}
    ,#{roleExplnCn}
    ,#{useYn}
    ,#{wrtrDeptNm}
    ,#{mdfrDeptNm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(role_cd)
DO UPDATE SET
     role_nm = coalesce(excluded.role_nm, "KIDS_OWN"."TB_PP_M_ROLE".role_nm)
    ,role_type = coalesce(excluded.role_type, "KIDS_OWN"."TB_PP_M_ROLE".role_type)
    ,role_expln_cn = coalesce(excluded.role_expln_cn, "KIDS_OWN"."TB_PP_M_ROLE".role_expln_cn)
    ,use_yn = coalesce(excluded.use_yn, "KIDS_OWN"."TB_PP_M_ROLE".use_yn)
    ,wrtr_dept_nm = coalesce(excluded.wrtr_dept_nm, "KIDS_OWN"."TB_PP_M_ROLE".wrtr_dept_nm)
    ,mdfr_dept_nm = coalesce(excluded.mdfr_dept_nm, "KIDS_OWN"."TB_PP_M_ROLE".mdfr_dept_nm)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_ROLE".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_ROLE".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_역할기본 삭제 (RoleMapper.deleteRole) */
DELETE FROM "KIDS_OWN"."TB_PP_M_ROLE"
WHERE 1=1
AND role_cd = #{roleCd}
