mapper.select:
/* 공통_공통코드기본 조회 (ComCdMapper.getComCd) */
SELECT
     com_cd
FROM kids_own.TB_CA_M_COM_CD
WHERE 1=1
AND com_cd = #{comCd}

mapper.insert:
/* 공통_공통코드기본 입력 (ComCdMapper.insertComCd) */
INSERT INTO kids_own.TB_CA_M_COM_CD
(
     com_cd
)
VALUES
(
     #{comCd}
)

mapper.update:
/* 공통_공통코드기본 수정 (ComCdMapper.updateComCd) */
UPDATE kids_own.TB_CA_M_COM_CD
SET
WHERE 1=1
AND com_cd = #{comCd}

mapper.insert:
/* 공통_공통코드기본 저장 (ComCdMapper.insertComCd) */
INSERT INTO kids_own.TB_CA_M_COM_CD
(
     com_cd
)
VALUES
(
     #{comCd}
)
ON CONFLICT(com_cd)
DO UPDATE SET

mapper.delete:
/* 공통_공통코드기본 삭제 (ComCdMapper.deleteComCd) */
DELETE FROM kids_own.TB_CA_M_COM_CD
WHERE 1=1
AND com_cd = #{comCd}
