mapper.insert:
/* 대국민포털_KIDS조직도기본 입력 (OrgchtMapper.insertOrgcht) */
INSERT INTO kids_own.TB_PP_M_ORGCHT
(
     emp_no
    ,dept_no
    ,dept_nm
    ,emp_nm
)
VALUES
(
     #{empNo}
    ,#{deptNo}
    ,#{deptNm}
    ,#{empNm}
)

/* 대국민포털_KIDS조직도기본 저장 (OrgchtMapper.insertOrgcht) */
INSERT INTO kids_own.TB_PP_M_ORGCHT
(
     emp_no
    ,dept_no
    ,dept_nm
    ,emp_nm
)
VALUES
(
     #{empNo}
    ,#{deptNo}
    ,#{deptNm}
    ,#{empNm}
)
ON CONFLICT(emp_no)
DO UPDATE SET
     dept_no = coalesce(excluded.dept_no, kids_own.TB_PP_M_ORGCHT.dept_no)
    ,dept_nm = coalesce(excluded.dept_nm, kids_own.TB_PP_M_ORGCHT.dept_nm)
    ,emp_nm = coalesce(excluded.emp_nm, kids_own.TB_PP_M_ORGCHT.emp_nm)

mapper.delete:
/* 대국민포털_KIDS조직도기본 삭제 (OrgchtMapper.deleteOrgcht) */
DELETE FROM kids_own.TB_PP_M_ORGCHT
WHERE 1=1
AND emp_no = #{empNo}
