mapper.insert:
/* 대국민포털_만족도조사기본 입력 (DgstfnExmnMapper.insertDgstfnExmn) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DGSTFN_EXMN"
(
     dgstfn_exmn_sn
    ,menu_sn
    ,dgstfn_scr
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{dgstfnExmnSn}
    ,#{menuSn}
    ,#{dgstfnScr}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_만족도조사기본 저장 (DgstfnExmnMapper.insertDgstfnExmn) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DGSTFN_EXMN"
(
     dgstfn_exmn_sn
    ,menu_sn
    ,dgstfn_scr
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{dgstfnExmnSn}
    ,#{menuSn}
    ,#{dgstfnScr}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(dgstfn_exmn_sn)
DO UPDATE SET
     menu_sn = coalesce(excluded.menu_sn, "KIDS_OWN"."TB_PP_M_DGSTFN_EXMN".menu_sn)
    ,dgstfn_scr = coalesce(excluded.dgstfn_scr, "KIDS_OWN"."TB_PP_M_DGSTFN_EXMN".dgstfn_scr)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_DGSTFN_EXMN".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_DGSTFN_EXMN".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_만족도조사기본 삭제 (DgstfnExmnMapper.deleteDgstfnExmn) */
DELETE FROM "KIDS_OWN"."TB_PP_M_DGSTFN_EXMN"
WHERE 1=1
AND dgstfn_exmn_sn = #{dgstfnExmnSn}
