mapper.insert:
/* 대국민포털_도메인기본 입력 (DmnMapper.insertDmn) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DMN"
(
     com_std_dmn_nm
    ,sys_se_nm
    ,artcl_sou_nm
    ,com_std_dmn_clsf_nm
    ,com_std_dmn_group_nm
    ,com_std_dmn_expln
    ,dmn_type_nm
    ,dmn_len
    ,dmn_dcpt_len
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{comStdDmnNm}
    ,#{sysSeNm}
    ,#{artclSouNm}
    ,#{comStdDmnClsfNm}
    ,#{comStdDmnGroupNm}
    ,#{comStdDmnExpln}
    ,#{dmnTypeNm}
    ,#{dmnLen}
    ,#{dmnDcptLen}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_도메인기본 저장 (DmnMapper.insertDmn) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DMN"
(
     com_std_dmn_nm
    ,sys_se_nm
    ,artcl_sou_nm
    ,com_std_dmn_clsf_nm
    ,com_std_dmn_group_nm
    ,com_std_dmn_expln
    ,dmn_type_nm
    ,dmn_len
    ,dmn_dcpt_len
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{comStdDmnNm}
    ,#{sysSeNm}
    ,#{artclSouNm}
    ,#{comStdDmnClsfNm}
    ,#{comStdDmnGroupNm}
    ,#{comStdDmnExpln}
    ,#{dmnTypeNm}
    ,#{dmnLen}
    ,#{dmnDcptLen}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(com_std_dmn_nm)
DO UPDATE SET
     sys_se_nm = coalesce(excluded.sys_se_nm, "KIDS_OWN"."TB_PP_M_DMN".sys_se_nm)
    ,artcl_sou_nm = coalesce(excluded.artcl_sou_nm, "KIDS_OWN"."TB_PP_M_DMN".artcl_sou_nm)
    ,com_std_dmn_clsf_nm = coalesce(excluded.com_std_dmn_clsf_nm, "KIDS_OWN"."TB_PP_M_DMN".com_std_dmn_clsf_nm)
    ,com_std_dmn_group_nm = coalesce(excluded.com_std_dmn_group_nm, "KIDS_OWN"."TB_PP_M_DMN".com_std_dmn_group_nm)
    ,com_std_dmn_expln = coalesce(excluded.com_std_dmn_expln, "KIDS_OWN"."TB_PP_M_DMN".com_std_dmn_expln)
    ,dmn_type_nm = coalesce(excluded.dmn_type_nm, "KIDS_OWN"."TB_PP_M_DMN".dmn_type_nm)
    ,dmn_len = coalesce(excluded.dmn_len, "KIDS_OWN"."TB_PP_M_DMN".dmn_len)
    ,dmn_dcpt_len = coalesce(excluded.dmn_dcpt_len, "KIDS_OWN"."TB_PP_M_DMN".dmn_dcpt_len)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_DMN".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_DMN".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_도메인기본 삭제 (DmnMapper.deleteDmn) */
DELETE FROM "KIDS_OWN"."TB_PP_M_DMN"
WHERE 1=1
AND com_std_dmn_nm = #{comStdDmnNm}
