mapper.select:
/* 대국민포털_DUR용량주의기본 조회 (DurCpctMapper.getDurCpct) */ SELECT cpct_cutn_sn ,mdcn_cd ,mdcn_nm ,gnrl_nm_cd ,gnrl_nm ,day_max_admin_cpct_cn ,daily_max_admin_crtr_cpct ,chck_crtr_igrd_cpct
,pbanc_ymd ,pbanc_no ,dtl_cn ,aplcn_ym
,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_DUR_CPCT WHERE 1=1 AND cpct_cutn_sn = #{cpctCutnSn}

mapper.insert:
/* 대국민포털_DUR용량주의기본 입력 (DurCpctMapper.insertDurCpct) */
INSERT INTO kids_own.TB_PP_M_DUR_CPCT
(
     cpct_cutn_sn
    ,mdcn_cd
    ,mdcn_nm
    ,gnrl_nm_cd
    ,gnrl_nm
    ,day_max_admin_cpct_cn
    ,daily_max_admin_crtr_cpct
    ,chck_crtr_igrd_cpct



    ,pbanc_ymd
    ,pbanc_no
    ,dtl_cn
    ,aplcn_ym

    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{cpctCutnSn}
    ,#{mdcnCd}
    ,#{mdcnNm}
    ,#{gnrlNmCd}
    ,#{gnrlNm}
    ,#{dayMaxAdminCpctCn}
    ,#{dailyMaxAdminCrtrCpct}
    ,#{chckCrtrIgrdCpct}



    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{dtlCn}
    ,#{aplcnYm}

    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

mapper.update:
/* 대국민포털_DUR용량주의기본 수정 (DurCpctMapper.updateDurCpct) */ UPDATE kids_own.TB_PP_M_DUR_CPCT SET mdcn_cd = coalesce(#{mdcnCd}, mdcn_cd) ,mdcn_nm = coalesce(#{mdcnNm}, mdcn_nm) ,gnrl_nm_cd = coalesce(#{gnrlNmCd}, gnrl_nm_cd) ,gnrl_nm = coalesce(#{gnrlNm}, gnrl_nm) ,day_max_admin_cpct_cn = coalesce(#{dayMaxAdminCpctCn}, day_max_admin_cpct_cn) ,daily_max_admin_crtr_cpct = coalesce(#{dailyMaxAdminCrtrCpct}, daily_max_admin_crtr_cpct) ,chck_crtr_igrd_cpct = coalesce(#{chckCrtrIgrdCpct}, chck_crtr_igrd_cpct)
,pbanc_ymd = coalesce(#{pbancYmd}, pbanc_ymd) ,pbanc_no = coalesce(#{pbancNo}, pbanc_no) ,dtl_cn = coalesce(#{dtlCn}, dtl_cn) ,aplcn_ym = coalesce(#{aplcnYm}, aplcn_ym)
,mdfr_id = coalesce(#{mdfrId}, mdfr_id) ,mdfcn_dt = now()
WHERE 1=1 AND cpct_cutn_sn = #{cpctCutnSn}

mapper.insert:
/* 대국민포털_DUR용량주의기본 저장 (DurCpctMapper.insertDurCpct) */
INSERT INTO kids_own.TB_PP_M_DUR_CPCT
(
     cpct_cutn_sn
    ,mdcn_cd
    ,mdcn_nm
    ,gnrl_nm_cd
    ,gnrl_nm
    ,day_max_admin_cpct_cn
    ,daily_max_admin_crtr_cpct
    ,chck_crtr_igrd_cpct



    ,pbanc_ymd
    ,pbanc_no
    ,dtl_cn
    ,aplcn_ym

    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{cpctCutnSn}
    ,#{mdcnCd}
    ,#{mdcnNm}
    ,#{gnrlNmCd}
    ,#{gnrlNm}
    ,#{dayMaxAdminCpctCn}
    ,#{dailyMaxAdminCrtrCpct}
    ,#{chckCrtrIgrdCpct}



    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{dtlCn}
    ,#{aplcnYm}

    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(cpct_cutn_sn)
DO UPDATE SET
     mdcn_cd = coalesce(excluded.mdcn_cd, kids_own.TB_PP_M_DUR_CPCT.mdcn_cd)
    ,mdcn_nm = coalesce(excluded.mdcn_nm, kids_own.TB_PP_M_DUR_CPCT.mdcn_nm)
    ,gnrl_nm_cd = coalesce(excluded.gnrl_nm_cd, kids_own.TB_PP_M_DUR_CPCT.gnrl_nm_cd)
    ,gnrl_nm = coalesce(excluded.gnrl_nm, kids_own.TB_PP_M_DUR_CPCT.gnrl_nm)
    ,day_max_admin_cpct_cn = coalesce(excluded.day_max_admin_cpct_cn, kids_own.TB_PP_M_DUR_CPCT.day_max_admin_cpct_cn)
    ,daily_max_admin_crtr_cpct = coalesce(excluded.daily_max_admin_crtr_cpct, kids_own.TB_PP_M_DUR_CPCT.daily_max_admin_crtr_cpct)
    ,chck_crtr_igrd_cpct = coalesce(excluded.chck_crtr_igrd_cpct, kids_own.TB_PP_M_DUR_CPCT.chck_crtr_igrd_cpct)



    ,pbanc_ymd = coalesce(excluded.pbanc_ymd, kids_own.TB_PP_M_DUR_CPCT.pbanc_ymd)
    ,pbanc_no = coalesce(excluded.pbanc_no, kids_own.TB_PP_M_DUR_CPCT.pbanc_no)
    ,dtl_cn = coalesce(excluded.dtl_cn, kids_own.TB_PP_M_DUR_CPCT.dtl_cn)
    ,aplcn_ym = coalesce(excluded.aplcn_ym, kids_own.TB_PP_M_DUR_CPCT.aplcn_ym)

    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_DUR_CPCT.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_DUR용량주의기본 삭제 (DurCpctMapper.deleteDurCpct) */
DELETE FROM kids_own.TB_PP_M_DUR_CPCT
WHERE 1=1
AND cpct_cutn_sn = #{cpctCutnSn}
