/* 대국민포털_DUR용량주의기본 저장 (DurCpctMapper.insertDurCpct) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DUR_CPCT"
(
     cpct_cutn_sn
    ,mdcn_cd
    ,mdcn_nm
    ,gnrl_nm_cd
    ,gnrl_nm
    ,daily_max_admin_cpct
    ,daily_max_admin_crtr_cpct
    ,chck_crtr_igrd_con
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,pbanc_ymd
    ,pbanc_no
    ,dtl_cn
    ,aplcn_ym
    ,type_cd
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
