(
     #{cpctCutnSn}
    ,#{mdcnCd}
    ,#{mdcnNm}
    ,#{gnrlNmCd}
    ,#{gnrlNm}
    ,#{dailyMaxAdminCpct}
    ,#{dailyMaxAdminCrtrCpct}
    ,#{chckCrtrIgrdCon}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{dtlCn}
    ,#{aplcnYm}
    ,#{typeCd}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(cpct_cutn_sn)
DO UPDATE SET
     mdcn_cd = coalesce(excluded.mdcn_cd, "KIDS_OWN"."TB_PP_M_DUR_CPCT".mdcn_cd)
    ,mdcn_nm = coalesce(excluded.mdcn_nm, "KIDS_OWN"."TB_PP_M_DUR_CPCT".mdcn_nm)
