    ,daily_max_admin_crtr_cpct = coalesce(excluded.daily_max_admin_crtr_cpct, "KIDS_OWN"."TB_PP_M_DUR_CPCT".daily_max_admin_crtr_cpct)
    ,chck_crtr_igrd_con = coalesce(excluded.chck_crtr_igrd_con, "KIDS_OWN"."TB_PP_M_DUR_CPCT".chck_crtr_igrd_con)
    ,mfds_prdct_nm = coalesce(excluded.mfds_prdct_nm, "KIDS_OWN"."TB_PP_M_DUR_CPCT".mfds_prdct_nm)
    ,mfds_igrd_cd = coalesce(excluded.mfds_igrd_cd, "KIDS_OWN"."TB_PP_M_DUR_CPCT".mfds_igrd_cd)
    ,mfds_igrd_nm = coalesce(excluded.mfds_igrd_nm, "KIDS_OWN"."TB_PP_M_DUR_CPCT".mfds_igrd_nm)
    ,pbanc_ymd = coalesce(excluded.pbanc_ymd, "KIDS_OWN"."TB_PP_M_DUR_CPCT".pbanc_ymd)
    ,pbanc_no = coalesce(excluded.pbanc_no, "KIDS_OWN"."TB_PP_M_DUR_CPCT".pbanc_no)
    ,dtl_cn = coalesce(excluded.dtl_cn, "KIDS_OWN"."TB_PP_M_DUR_CPCT".dtl_cn)
    ,aplcn_ym = coalesce(excluded.aplcn_ym, "KIDS_OWN"."TB_PP_M_DUR_CPCT".aplcn_ym)
    ,type_cd = coalesce(excluded.type_cd, "KIDS_OWN"."TB_PP_M_DUR_CPCT".type_cd)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_DUR_CPCT".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_DUR_CPCT".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_DUR용량주의기본 삭제 (DurCpctMapper.deleteDurCpct) */
DELETE FROM "KIDS_OWN"."TB_PP_M_DUR_CPCT"
WHERE 1=1
AND cpct_cutn_sn = #{cpctCutnSn}
