mapper.select:
/* 대국민포털_DUR투여기간주의기본 조회 (DurDosageMapper.getDurDosage) */ SELECT admin_prd_cutn_sn ,mdcn_cd ,mdcn_nm ,gnrl_nm_cd ,gnrl_nm ,max_admin_prd_day_cnt
,pbanc_ymd ,pbanc_no ,aplcn_ym ,rmrk_cn
,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_DUR_DOSAGE WHERE 1=1 AND admin_prd_cutn_sn = #{adminPrdCutnSn}

mapper.insert:
/* 대국민포털_DUR투여기간주의기본 입력 (DurDosageMapper.insertDurDosage) */
INSERT INTO kids_own.TB_PP_M_DUR_DOSAGE
(
     admin_prd_cutn_sn
    ,mdcn_cd
    ,mdcn_nm
    ,gnrl_nm_cd
    ,gnrl_nm
    ,max_admin_prd_day_cnt



    ,pbanc_ymd
    ,pbanc_no
    ,aplcn_ym
    ,rmrk_cn

    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{adminPrdCutnSn}
    ,#{mdcnCd}
    ,#{mdcnNm}
    ,#{gnrlNmCd}
    ,#{gnrlNm}
    ,#{maxAdminPrdDayCnt}



    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{aplcnYm}
    ,#{rmrk_cn}

    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

mapper.update:
/* 대국민포털_DUR투여기간주의기본 수정 (DurDosageMapper.updateDurDosage) */ UPDATE kids_own.TB_PP_M_DUR_DOSAGE SET mdcn_cd = coalesce(#{mdcnCd}, mdcn_cd) ,mdcn_nm = coalesce(#{mdcnNm}, mdcn_nm) ,gnrl_nm_cd = coalesce(#{gnrlNmCd}, gnrl_nm_cd) ,gnrl_nm = coalesce(#{gnrlNm}, gnrl_nm) ,max_admin_prd_day_cnt = coalesce(#{maxAdminPrdDayCnt}, max_admin_prd_day_cnt)
,pbanc_ymd = coalesce(#{pbancYmd}, pbanc_ymd) ,pbanc_no = coalesce(#{pbancNo}, pbanc_no) ,aplcn_ym = coalesce(#{aplcnYm}, aplcn_ym) ,rmrk_cn = coalesce(#{rmrk_cn}, rmrk_cn)
,mdfr_id = coalesce(#{mdfrId}, mdfr_id) ,mdfcn_dt = now()
WHERE 1=1 AND admin_prd_cutn_sn = #{adminPrdCutnSn}

mapper.insert:
/* 대국민포털_DUR투여기간주의기본 저장 (DurDosageMapper.insertDurDosage) */
INSERT INTO kids_own.TB_PP_M_DUR_DOSAGE
(
     admin_prd_cutn_sn
    ,mdcn_cd
    ,mdcn_nm
    ,gnrl_nm_cd
    ,gnrl_nm
    ,max_admin_prd_day_cnt



    ,pbanc_ymd
    ,pbanc_no
    ,aplcn_ym
    ,rmrk_cn

    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{adminPrdCutnSn}
    ,#{mdcnCd}
    ,#{mdcnNm}
    ,#{gnrlNmCd}
    ,#{gnrlNm}
    ,#{maxAdminPrdDayCnt}



    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{aplcnYm}
    ,#{rmrk_cn}

    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(admin_prd_cutn_sn)
DO UPDATE SET
     mdcn_cd = coalesce(excluded.mdcn_cd, kids_own.TB_PP_M_DUR_DOSAGE.mdcn_cd)
    ,mdcn_nm = coalesce(excluded.mdcn_nm, kids_own.TB_PP_M_DUR_DOSAGE.mdcn_nm)
    ,gnrl_nm_cd = coalesce(excluded.gnrl_nm_cd, kids_own.TB_PP_M_DUR_DOSAGE.gnrl_nm_cd)
    ,gnrl_nm = coalesce(excluded.gnrl_nm, kids_own.TB_PP_M_DUR_DOSAGE.gnrl_nm)
    ,max_admin_prd_day_cnt = coalesce(excluded.max_admin_prd_day_cnt, kids_own.TB_PP_M_DUR_DOSAGE.max_admin_prd_day_cnt)



    ,pbanc_ymd = coalesce(excluded.pbanc_ymd, kids_own.TB_PP_M_DUR_DOSAGE.pbanc_ymd)
    ,pbanc_no = coalesce(excluded.pbanc_no, kids_own.TB_PP_M_DUR_DOSAGE.pbanc_no)
    ,aplcn_ym = coalesce(excluded.aplcn_ym, kids_own.TB_PP_M_DUR_DOSAGE.aplcn_ym)
    ,rmrk_cn = coalesce(excluded.rmrk_cn, kids_own.TB_PP_M_DUR_DOSAGE.rmrk_cn)

    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_DUR_DOSAGE.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_DUR투여기간주의기본 삭제 (DurDosageMapper.deleteDurDosage) */
DELETE FROM kids_own.TB_PP_M_DUR_DOSAGE
WHERE 1=1
AND admin_prd_cutn_sn = #{adminPrdCutnSn}
