mapper.insert:
/* 대국민포털_DUR투여기간주의기본 입력 (DurDosageMapper.insertDurDosage) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DUR_DOSAGE"
(
     dosage_prd_cutn_sn
    ,mdcn_cd
    ,mdcn_nm
    ,gnrl_nm_cd
    ,gnrl_nm
    ,max_dosage_prd_days
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,pbanc_ymd
    ,pbanc_no
    ,aplcn_ym
    ,rmrk
    ,type_cd
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{dosagePrdCutnSn}
    ,#{mdcnCd}
    ,#{mdcnNm}
    ,#{gnrlNmCd}
    ,#{gnrlNm}
    ,#{maxDosagePrdDays}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{aplcnYm}
    ,#{rmrk}
    ,#{typeCd}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_DUR투여기간주의기본 저장 (DurDosageMapper.insertDurDosage) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DUR_DOSAGE"
(
     dosage_prd_cutn_sn
    ,mdcn_cd
    ,mdcn_nm
    ,gnrl_nm_cd
    ,gnrl_nm
    ,max_dosage_prd_days
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,pbanc_ymd
    ,pbanc_no
    ,aplcn_ym
    ,rmrk
    ,type_cd
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{dosagePrdCutnSn}
    ,#{mdcnCd}
    ,#{mdcnNm}
    ,#{gnrlNmCd}
    ,#{gnrlNm}
    ,#{maxDosagePrdDays}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{aplcnYm}
    ,#{rmrk}
    ,#{typeCd}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(dosage_prd_cutn_sn)
DO UPDATE SET
     mdcn_cd = coalesce(excluded.mdcn_cd, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".mdcn_cd)
    ,mdcn_nm = coalesce(excluded.mdcn_nm, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".mdcn_nm)
    ,gnrl_nm_cd = coalesce(excluded.gnrl_nm_cd, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".gnrl_nm_cd)
    ,gnrl_nm = coalesce(excluded.gnrl_nm, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".gnrl_nm)
    ,max_dosage_prd_days = coalesce(excluded.max_dosage_prd_days, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".max_dosage_prd_days)
    ,mfds_prdct_nm = coalesce(excluded.mfds_prdct_nm, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".mfds_prdct_nm)
    ,mfds_igrd_cd = coalesce(excluded.mfds_igrd_cd, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".mfds_igrd_cd)
    ,mfds_igrd_nm = coalesce(excluded.mfds_igrd_nm, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".mfds_igrd_nm)
    ,pbanc_ymd = coalesce(excluded.pbanc_ymd, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".pbanc_ymd)
    ,pbanc_no = coalesce(excluded.pbanc_no, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".pbanc_no)
    ,aplcn_ym = coalesce(excluded.aplcn_ym, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".aplcn_ym)
    ,rmrk = coalesce(excluded.rmrk, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".rmrk)
    ,type_cd = coalesce(excluded.type_cd, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".type_cd)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_DUR_DOSAGE".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_DUR투여기간주의기본 삭제 (DurDosageMapper.deleteDurDosage) */
DELETE FROM "KIDS_OWN"."TB_PP_M_DUR_DOSAGE"
WHERE 1=1
AND dosage_prd_cutn_sn = #{dosagePrdCutnSn}
