mapper.select:
/* 대국민포털_DUR효능군중복성분기본 조회 (DurEftgrpIgrdMapper.getDurEftgrpIgrd) */ SELECT eftgrp_dpcn_sn ,eftgrp_nm ,bsys_nm ,korn_igrd_nm ,eng_igrd_nm ,rmrk ,mfds_prdct_nm ,mfds_igrd_cd ,mfds_igrd_nm ,aplcn_ym ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_DUR_EFTGRP_IGRD WHERE 1=1 AND eftgrp_dpcn_sn = #{eftgrpDpcnSn}

mapper.insert:
/* 대국민포털_DUR효능군중복성분기본 입력 (DurEftgrpIgrdMapper.insertDurEftgrpIgrd) */
INSERT INTO kids_own.TB_PP_M_DUR_EFTGRP_IGRD
(
     eftgrp_dpcn_sn
    ,eftgrp_nm
    ,bsys_nm
    ,korn_igrd_nm
    ,eng_igrd_nm
    ,rmrk
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,aplcn_ym
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{eftgrpDpcnSn}
    ,#{eftgrpNm}
    ,#{bsysNm}
    ,#{kornIgrdNm}
    ,#{engIgrdNm}
    ,#{rmrk}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{aplcnYm}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_DUR효능군중복성분기본 저장 (DurEftgrpIgrdMapper.insertDurEftgrpIgrd) */
INSERT INTO kids_own.TB_PP_M_DUR_EFTGRP_IGRD
(
     eftgrp_dpcn_sn
    ,eftgrp_nm
    ,bsys_nm
    ,korn_igrd_nm
    ,eng_igrd_nm
    ,rmrk
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,aplcn_ym
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{eftgrpDpcnSn}
    ,#{eftgrpNm}
    ,#{bsysNm}
    ,#{kornIgrdNm}
    ,#{engIgrdNm}
    ,#{rmrk}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{aplcnYm}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(eftgrp_dpcn_sn)
DO UPDATE SET
     eftgrp_nm = coalesce(excluded.eftgrp_nm, kids_own.TB_PP_M_DUR_EFTGRP_IGRD.eftgrp_nm)
    ,bsys_nm = coalesce(excluded.bsys_nm, kids_own.TB_PP_M_DUR_EFTGRP_IGRD.bsys_nm)
    ,korn_igrd_nm = coalesce(excluded.korn_igrd_nm, kids_own.TB_PP_M_DUR_EFTGRP_IGRD.korn_igrd_nm)
    ,eng_igrd_nm = coalesce(excluded.eng_igrd_nm, kids_own.TB_PP_M_DUR_EFTGRP_IGRD.eng_igrd_nm)
    ,rmrk = coalesce(excluded.rmrk, kids_own.TB_PP_M_DUR_EFTGRP_IGRD.rmrk)
    ,mfds_prdct_nm = coalesce(excluded.mfds_prdct_nm, kids_own.TB_PP_M_DUR_EFTGRP_IGRD.mfds_prdct_nm)
    ,mfds_igrd_cd = coalesce(excluded.mfds_igrd_cd, kids_own.TB_PP_M_DUR_EFTGRP_IGRD.mfds_igrd_cd)
    ,mfds_igrd_nm = coalesce(excluded.mfds_igrd_nm, kids_own.TB_PP_M_DUR_EFTGRP_IGRD.mfds_igrd_nm)
    ,aplcn_ym = coalesce(excluded.aplcn_ym, kids_own.TB_PP_M_DUR_EFTGRP_IGRD.aplcn_ym)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_DUR_EFTGRP_IGRD.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_DUR효능군중복성분기본 삭제 (DurEftgrpIgrdMapper.deleteDurEftgrpIgrd) */
DELETE FROM kids_own.TB_PP_M_DUR_EFTGRP_IGRD
WHERE 1=1
AND eftgrp_dpcn_sn = #{eftgrpDpcnSn}
