mapper.insert:
/* 대국민포털_DUR효능군중복품목기본 입력 (DurEftgrpItemMapper.insertDurEftgrpItem) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM"
(
     eftgrp_dpcn_sn
    ,mdcn_cd
    ,eft_group
    ,group
    ,group_1
    ,eftgrp_dpcn_chck_cd
    ,gnrl_nm_cd
    ,gnrl_nm
    ,item_nm
    ,bzenty_nm
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,pbanc_ymd
    ,pbanc_no
    ,aplcn_ym
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{eftgrpDpcnSn}
    ,#{mdcnCd}
    ,#{eftGroup}
    ,#{group}
    ,#{group1}
    ,#{eftgrpDpcnChckCd}
    ,#{gnrlNmCd}
    ,#{gnrlNm}
    ,#{itemNm}
    ,#{bzentyNm}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{aplcnYm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_DUR효능군중복품목기본 저장 (DurEftgrpItemMapper.insertDurEftgrpItem) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM"
(
     eftgrp_dpcn_sn
    ,mdcn_cd
    ,eft_group
    ,group
    ,group_1
    ,eftgrp_dpcn_chck_cd
    ,gnrl_nm_cd
    ,gnrl_nm
    ,item_nm
    ,bzenty_nm
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,pbanc_ymd
    ,pbanc_no
    ,aplcn_ym
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{eftgrpDpcnSn}
    ,#{mdcnCd}
    ,#{eftGroup}
    ,#{group}
    ,#{group1}
    ,#{eftgrpDpcnChckCd}
    ,#{gnrlNmCd}
    ,#{gnrlNm}
    ,#{itemNm}
    ,#{bzentyNm}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{aplcnYm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(eftgrp_dpcn_sn)
DO UPDATE SET
     mdcn_cd = coalesce(excluded.mdcn_cd, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".mdcn_cd)
    ,eft_group = coalesce(excluded.eft_group, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".eft_group)
    ,group = coalesce(excluded.group, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".group)
    ,group_1 = coalesce(excluded.group_1, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".group_1)
    ,eftgrp_dpcn_chck_cd = coalesce(excluded.eftgrp_dpcn_chck_cd, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".eftgrp_dpcn_chck_cd)
    ,gnrl_nm_cd = coalesce(excluded.gnrl_nm_cd, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".gnrl_nm_cd)
    ,gnrl_nm = coalesce(excluded.gnrl_nm, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".gnrl_nm)
    ,item_nm = coalesce(excluded.item_nm, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".item_nm)
    ,bzenty_nm = coalesce(excluded.bzenty_nm, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".bzenty_nm)
    ,mfds_prdct_nm = coalesce(excluded.mfds_prdct_nm, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".mfds_prdct_nm)
    ,mfds_igrd_cd = coalesce(excluded.mfds_igrd_cd, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".mfds_igrd_cd)
    ,mfds_igrd_nm = coalesce(excluded.mfds_igrd_nm, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".mfds_igrd_nm)
    ,pbanc_ymd = coalesce(excluded.pbanc_ymd, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".pbanc_ymd)
    ,pbanc_no = coalesce(excluded.pbanc_no, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".pbanc_no)
    ,aplcn_ym = coalesce(excluded.aplcn_ym, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".aplcn_ym)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_DUR효능군중복품목기본 삭제 (DurEftgrpItemMapper.deleteDurEftgrpItem) */
DELETE FROM "KIDS_OWN"."TB_PP_M_DUR_EFTGRP_ITEM"
WHERE 1=1
AND eftgrp_dpcn_sn = #{eftgrpDpcnSn}
