mapper.insert:
/* 대국민포털_DUR수유부주의기본 입력 (DurNurswMapper.insertDurNursw) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DUR_NURSW"
(
     nursw_cutn_sn
    ,prdct_cd
    ,main_igrd_cd
    ,igrd_nm
    ,prdct_nm
    ,bzenty_nm
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,pbanc_ymd
    ,pbanc_no
    ,aplcn_ym
    ,rmrk
    ,type_cd
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{nurswCutnSn}
    ,#{prdctCd}
    ,#{mainIgrdCd}
    ,#{igrdNm}
    ,#{prdctNm}
    ,#{bzentyNm}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{aplcnYm}
    ,#{rmrk}
    ,#{typeCd}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_DUR수유부주의기본 저장 (DurNurswMapper.insertDurNursw) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DUR_NURSW"
(
     nursw_cutn_sn
    ,prdct_cd
    ,main_igrd_cd
    ,igrd_nm
    ,prdct_nm
    ,bzenty_nm
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,pbanc_ymd
    ,pbanc_no
    ,aplcn_ym
    ,rmrk
    ,type_cd
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{nurswCutnSn}
    ,#{prdctCd}
    ,#{mainIgrdCd}
    ,#{igrdNm}
    ,#{prdctNm}
    ,#{bzentyNm}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{aplcnYm}
    ,#{rmrk}
    ,#{typeCd}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(nursw_cutn_sn)
DO UPDATE SET
     prdct_cd = coalesce(excluded.prdct_cd, "KIDS_OWN"."TB_PP_M_DUR_NURSW".prdct_cd)
    ,main_igrd_cd = coalesce(excluded.main_igrd_cd, "KIDS_OWN"."TB_PP_M_DUR_NURSW".main_igrd_cd)
    ,igrd_nm = coalesce(excluded.igrd_nm, "KIDS_OWN"."TB_PP_M_DUR_NURSW".igrd_nm)
    ,prdct_nm = coalesce(excluded.prdct_nm, "KIDS_OWN"."TB_PP_M_DUR_NURSW".prdct_nm)
    ,bzenty_nm = coalesce(excluded.bzenty_nm, "KIDS_OWN"."TB_PP_M_DUR_NURSW".bzenty_nm)
    ,mfds_prdct_nm = coalesce(excluded.mfds_prdct_nm, "KIDS_OWN"."TB_PP_M_DUR_NURSW".mfds_prdct_nm)
    ,mfds_igrd_cd = coalesce(excluded.mfds_igrd_cd, "KIDS_OWN"."TB_PP_M_DUR_NURSW".mfds_igrd_cd)
    ,mfds_igrd_nm = coalesce(excluded.mfds_igrd_nm, "KIDS_OWN"."TB_PP_M_DUR_NURSW".mfds_igrd_nm)
    ,pbanc_ymd = coalesce(excluded.pbanc_ymd, "KIDS_OWN"."TB_PP_M_DUR_NURSW".pbanc_ymd)
    ,pbanc_no = coalesce(excluded.pbanc_no, "KIDS_OWN"."TB_PP_M_DUR_NURSW".pbanc_no)
    ,aplcn_ym = coalesce(excluded.aplcn_ym, "KIDS_OWN"."TB_PP_M_DUR_NURSW".aplcn_ym)
    ,rmrk = coalesce(excluded.rmrk, "KIDS_OWN"."TB_PP_M_DUR_NURSW".rmrk)
    ,type_cd = coalesce(excluded.type_cd, "KIDS_OWN"."TB_PP_M_DUR_NURSW".type_cd)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_DUR_NURSW".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_DUR_NURSW".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_DUR수유부주의기본 삭제 (DurNurswMapper.deleteDurNursw) */
DELETE FROM "KIDS_OWN"."TB_PP_M_DUR_NURSW"
WHERE 1=1
AND nursw_cutn_sn = #{nurswCutnSn}
