mapper.insert:
/* 대국민포털_DUR임부금기성분기본 입력 (DurPrgntIgrdMapper.insertDurPrgntIgrd) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD"
(
     prgnt_bann_sn
    ,igrd_nm
    ,bann_grd
    ,rmrk
    ,dtl_cn
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,aplcn_ym
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{prgntBannSn}
    ,#{igrdNm}
    ,#{bannGrd}
    ,#{rmrk}
    ,#{dtlCn}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{aplcnYm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_DUR임부금기성분기본 저장 (DurPrgntIgrdMapper.insertDurPrgntIgrd) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD"
(
     prgnt_bann_sn
    ,igrd_nm
    ,bann_grd
    ,rmrk
    ,dtl_cn
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,aplcn_ym
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{prgntBannSn}
    ,#{igrdNm}
    ,#{bannGrd}
    ,#{rmrk}
    ,#{dtlCn}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{aplcnYm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(prgnt_bann_sn)
DO UPDATE SET
     igrd_nm = coalesce(excluded.igrd_nm, "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD".igrd_nm)
    ,bann_grd = coalesce(excluded.bann_grd, "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD".bann_grd)
    ,rmrk = coalesce(excluded.rmrk, "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD".rmrk)
    ,dtl_cn = coalesce(excluded.dtl_cn, "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD".dtl_cn)
    ,mfds_prdct_nm = coalesce(excluded.mfds_prdct_nm, "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD".mfds_prdct_nm)
    ,mfds_igrd_cd = coalesce(excluded.mfds_igrd_cd, "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD".mfds_igrd_cd)
    ,mfds_igrd_nm = coalesce(excluded.mfds_igrd_nm, "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD".mfds_igrd_nm)
    ,aplcn_ym = coalesce(excluded.aplcn_ym, "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD".aplcn_ym)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_DUR임부금기성분기본 삭제 (DurPrgntIgrdMapper.deleteDurPrgntIgrd) */
DELETE FROM "KIDS_OWN"."TB_PP_M_DUR_PRGNT_IGRD"
WHERE 1=1
AND prgnt_bann_sn = #{prgntBannSn}
