mapper.select:
/* 대국민포털_DUR임부금기품목기본 조회 (DurPrgntItemMapper.getDurPrgntItem) */ SELECT prgnt_bann_sn ,prdct_cd ,igrd_nm ,igrd_cd ,prdct_nm ,bzenty_nm ,mfds_prdct_nm ,mfds_igrd_cd ,mfds_igrd_nm ,ancmnt_ymd ,ancmnt_no ,bann_grd ,dtl_info ,aplcn_ym ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_DUR_PRGNT_ITEM WHERE 1=1 AND prgnt_bann_sn = #{prgntBannSn}

mapper.insert:
/* 대국민포털_DUR임부금기품목기본 입력 (DurPrgntItemMapper.insertDurPrgntItem) */
INSERT INTO kids_own.TB_PP_M_DUR_PRGNT_ITEM
(
     prgnt_bann_sn
    ,prdct_cd
    ,igrd_nm
    ,igrd_cd
    ,prdct_nm
    ,bzenty_nm
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,ancmnt_ymd
    ,ancmnt_no
    ,bann_grd
    ,dtl_info
    ,aplcn_ym
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{prgntBannSn}
    ,#{prdctCd}
    ,#{igrdNm}
    ,#{igrdCd}
    ,#{prdctNm}
    ,#{bzentyNm}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{ancmntYmd}
    ,#{ancmntNo}
    ,#{bannGrd}
    ,#{dtlInfo}
    ,#{aplcnYm}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_DUR임부금기품목기본 저장 (DurPrgntItemMapper.insertDurPrgntItem) */
INSERT INTO kids_own.TB_PP_M_DUR_PRGNT_ITEM
(
     prgnt_bann_sn
    ,prdct_cd
    ,igrd_nm
    ,igrd_cd
    ,prdct_nm
    ,bzenty_nm
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,ancmnt_ymd
    ,ancmnt_no
    ,bann_grd
    ,dtl_info
    ,aplcn_ym
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{prgntBannSn}
    ,#{prdctCd}
    ,#{igrdNm}
    ,#{igrdCd}
    ,#{prdctNm}
    ,#{bzentyNm}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{ancmntYmd}
    ,#{ancmntNo}
    ,#{bannGrd}
    ,#{dtlInfo}
    ,#{aplcnYm}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(prgnt_bann_sn)
DO UPDATE SET
     prdct_cd = coalesce(excluded.prdct_cd, kids_own.TB_PP_M_DUR_PRGNT_ITEM.prdct_cd)
    ,igrd_nm = coalesce(excluded.igrd_nm, kids_own.TB_PP_M_DUR_PRGNT_ITEM.igrd_nm)
    ,igrd_cd = coalesce(excluded.igrd_cd, kids_own.TB_PP_M_DUR_PRGNT_ITEM.igrd_cd)
    ,prdct_nm = coalesce(excluded.prdct_nm, kids_own.TB_PP_M_DUR_PRGNT_ITEM.prdct_nm)
    ,bzenty_nm = coalesce(excluded.bzenty_nm, kids_own.TB_PP_M_DUR_PRGNT_ITEM.bzenty_nm)
    ,mfds_prdct_nm = coalesce(excluded.mfds_prdct_nm, kids_own.TB_PP_M_DUR_PRGNT_ITEM.mfds_prdct_nm)
    ,mfds_igrd_cd = coalesce(excluded.mfds_igrd_cd, kids_own.TB_PP_M_DUR_PRGNT_ITEM.mfds_igrd_cd)
    ,mfds_igrd_nm = coalesce(excluded.mfds_igrd_nm, kids_own.TB_PP_M_DUR_PRGNT_ITEM.mfds_igrd_nm)
    ,ancmnt_ymd = coalesce(excluded.ancmnt_ymd, kids_own.TB_PP_M_DUR_PRGNT_ITEM.ancmnt_ymd)
    ,ancmnt_no = coalesce(excluded.ancmnt_no, kids_own.TB_PP_M_DUR_PRGNT_ITEM.ancmnt_no)
    ,bann_grd = coalesce(excluded.bann_grd, kids_own.TB_PP_M_DUR_PRGNT_ITEM.bann_grd)
    ,dtl_info = coalesce(excluded.dtl_info, kids_own.TB_PP_M_DUR_PRGNT_ITEM.dtl_info)
    ,aplcn_ym = coalesce(excluded.aplcn_ym, kids_own.TB_PP_M_DUR_PRGNT_ITEM.aplcn_ym)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_DUR_PRGNT_ITEM.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_DUR임부금기품목기본 삭제 (DurPrgntItemMapper.deleteDurPrgntItem) */
DELETE FROM kids_own.TB_PP_M_DUR_PRGNT_ITEM
WHERE 1=1
AND prgnt_bann_sn = #{prgntBannSn}
