mapper.select:
/* 대국민포털_DUR노인주의기본 조회 (DurSnctzMapper.getDurSnctz) */ SELECT snctz_cutn_sn ,prdct_cd ,dur_igrd_nm ,dur_igrd_cd ,dur_prdct_nm ,dur_bzenty_nm ,mfds_prdct_nm ,mfds_igrd_cd ,mfds_igrd_nm ,pbanc_ymd ,pbanc_no ,mdcn_dtl_info ,aplcn_ym ,rmrk ,type_cd ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_DUR_SNCTZ WHERE 1=1 AND snctz_cutn_sn = #{snctzCutnSn}

mapper.insert:
/* 대국민포털_DUR노인주의기본 입력 (DurSnctzMapper.insertDurSnctz) */
INSERT INTO kids_own.TB_PP_M_DUR_SNCTZ
(
     snctz_cutn_sn
    ,prdct_cd
    ,dur_igrd_nm
    ,dur_igrd_cd
    ,dur_prdct_nm
    ,dur_bzenty_nm
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,pbanc_ymd
    ,pbanc_no
    ,mdcn_dtl_info
    ,aplcn_ym
    ,rmrk
    ,type_cd
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{snctzCutnSn}
    ,#{prdctCd}
    ,#{durIgrdNm}
    ,#{durIgrdCd}
    ,#{durPrdctNm}
    ,#{durBzentyNm}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{mdcnDtlInfo}
    ,#{aplcnYm}
    ,#{rmrk}
    ,#{typeCd}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_DUR노인주의기본 저장 (DurSnctzMapper.insertDurSnctz) */
INSERT INTO kids_own.TB_PP_M_DUR_SNCTZ
(
     snctz_cutn_sn
    ,prdct_cd
    ,dur_igrd_nm
    ,dur_igrd_cd
    ,dur_prdct_nm
    ,dur_bzenty_nm
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,pbanc_ymd
    ,pbanc_no
    ,mdcn_dtl_info
    ,aplcn_ym
    ,rmrk
    ,type_cd
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{snctzCutnSn}
    ,#{prdctCd}
    ,#{durIgrdNm}
    ,#{durIgrdCd}
    ,#{durPrdctNm}
    ,#{durBzentyNm}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{pbancYmd}
    ,#{pbancNo}
    ,#{mdcnDtlInfo}
    ,#{aplcnYm}
    ,#{rmrk}
    ,#{typeCd}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(snctz_cutn_sn)
DO UPDATE SET
     prdct_cd = coalesce(excluded.prdct_cd, kids_own.TB_PP_M_DUR_SNCTZ.prdct_cd)
    ,dur_igrd_nm = coalesce(excluded.dur_igrd_nm, kids_own.TB_PP_M_DUR_SNCTZ.dur_igrd_nm)
    ,dur_igrd_cd = coalesce(excluded.dur_igrd_cd, kids_own.TB_PP_M_DUR_SNCTZ.dur_igrd_cd)
    ,dur_prdct_nm = coalesce(excluded.dur_prdct_nm, kids_own.TB_PP_M_DUR_SNCTZ.dur_prdct_nm)
    ,dur_bzenty_nm = coalesce(excluded.dur_bzenty_nm, kids_own.TB_PP_M_DUR_SNCTZ.dur_bzenty_nm)
    ,mfds_prdct_nm = coalesce(excluded.mfds_prdct_nm, kids_own.TB_PP_M_DUR_SNCTZ.mfds_prdct_nm)
    ,mfds_igrd_cd = coalesce(excluded.mfds_igrd_cd, kids_own.TB_PP_M_DUR_SNCTZ.mfds_igrd_cd)
    ,mfds_igrd_nm = coalesce(excluded.mfds_igrd_nm, kids_own.TB_PP_M_DUR_SNCTZ.mfds_igrd_nm)
    ,pbanc_ymd = coalesce(excluded.pbanc_ymd, kids_own.TB_PP_M_DUR_SNCTZ.pbanc_ymd)
    ,pbanc_no = coalesce(excluded.pbanc_no, kids_own.TB_PP_M_DUR_SNCTZ.pbanc_no)
    ,mdcn_dtl_info = coalesce(excluded.mdcn_dtl_info, kids_own.TB_PP_M_DUR_SNCTZ.mdcn_dtl_info)
    ,aplcn_ym = coalesce(excluded.aplcn_ym, kids_own.TB_PP_M_DUR_SNCTZ.aplcn_ym)
    ,rmrk = coalesce(excluded.rmrk, kids_own.TB_PP_M_DUR_SNCTZ.rmrk)
    ,type_cd = coalesce(excluded.type_cd, kids_own.TB_PP_M_DUR_SNCTZ.type_cd)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_DUR_SNCTZ.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_DUR노인주의기본 삭제 (DurSnctzMapper.deleteDurSnctz) */
DELETE FROM kids_own.TB_PP_M_DUR_SNCTZ
WHERE 1=1
AND snctz_cutn_sn = #{snctzCutnSn}
