mapper.insert:
/* 대국민포털_DUR노인주의해열진통소염제기본 입력 (DurSnctzMedMapper.insertDurSnctzMed) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED"
(
     snctz_cutn_med_sn
    ,prdct_cd
    ,igrd_cd
    ,igrd_nm
    ,prdct_nm
    ,entp_nm
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,mdcn_dtl_info
    ,aplcn_ym
    ,type_cd
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{snctzCutnMedSn}
    ,#{prdctCd}
    ,#{igrdCd}
    ,#{igrdNm}
    ,#{prdctNm}
    ,#{entpNm}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{mdcnDtlInfo}
    ,#{aplcnYm}
    ,#{typeCd}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_DUR노인주의해열진통소염제기본 저장 (DurSnctzMedMapper.insertDurSnctzMed) */
INSERT INTO "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED"
(
     snctz_cutn_med_sn
    ,prdct_cd
    ,igrd_cd
    ,igrd_nm
    ,prdct_nm
    ,entp_nm
    ,mfds_prdct_nm
    ,mfds_igrd_cd
    ,mfds_igrd_nm
    ,mdcn_dtl_info
    ,aplcn_ym
    ,type_cd
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{snctzCutnMedSn}
    ,#{prdctCd}
    ,#{igrdCd}
    ,#{igrdNm}
    ,#{prdctNm}
    ,#{entpNm}
    ,#{mfdsPrdctNm}
    ,#{mfdsIgrdCd}
    ,#{mfdsIgrdNm}
    ,#{mdcnDtlInfo}
    ,#{aplcnYm}
    ,#{typeCd}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(snctz_cutn_med_sn)
DO UPDATE SET
     prdct_cd = coalesce(excluded.prdct_cd, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".prdct_cd)
    ,igrd_cd = coalesce(excluded.igrd_cd, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".igrd_cd)
    ,igrd_nm = coalesce(excluded.igrd_nm, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".igrd_nm)
    ,prdct_nm = coalesce(excluded.prdct_nm, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".prdct_nm)
    ,entp_nm = coalesce(excluded.entp_nm, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".entp_nm)
    ,mfds_prdct_nm = coalesce(excluded.mfds_prdct_nm, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".mfds_prdct_nm)
    ,mfds_igrd_cd = coalesce(excluded.mfds_igrd_cd, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".mfds_igrd_cd)
    ,mfds_igrd_nm = coalesce(excluded.mfds_igrd_nm, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".mfds_igrd_nm)
    ,mdcn_dtl_info = coalesce(excluded.mdcn_dtl_info, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".mdcn_dtl_info)
    ,aplcn_ym = coalesce(excluded.aplcn_ym, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".aplcn_ym)
    ,type_cd = coalesce(excluded.type_cd, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".type_cd)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_DUR노인주의해열진통소염제기본 삭제 (DurSnctzMedMapper.deleteDurSnctzMed) */
DELETE FROM "KIDS_OWN"."TB_PP_M_DUR_SNCTZ_MED"
WHERE 1=1
AND snctz_cutn_med_sn = #{snctzCutnMedSn}
