mapper.select:
/* 대국민포털_관리자정보기본 조회 (EmpMapper.getEmp) */ SELECT emp_no ,mngr_enpswd ,tmpr_pswd_yn ,pswd_err_nmtm ,encpt_bfr_pswd ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_EMP WHERE 1=1 AND emp_no = #{empNo}

mapper.insert:
/* 대국민포털_관리자정보기본 입력 (EmpMapper.insertEmp) */
INSERT INTO kids_own.TB_PP_M_EMP
(
     emp_no
    ,mngr_enpswd
    ,tmpr_pswd_yn
    ,pswd_err_nmtm
    ,encpt_bfr_pswd
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{empNo}
    ,#{mngrEnpswd}
    ,#{tmprPswdYn}
    ,#{pswdErrNmtm}
    ,#{encptBfrPswd}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_관리자정보기본 저장 (EmpMapper.insertEmp) */
INSERT INTO kids_own.TB_PP_M_EMP
(
     emp_no
    ,mngr_enpswd
    ,tmpr_pswd_yn
    ,pswd_err_nmtm
    ,encpt_bfr_pswd
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{empNo}
    ,#{mngrEnpswd}
    ,#{tmprPswdYn}
    ,#{pswdErrNmtm}
    ,#{encptBfrPswd}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(emp_no)
DO UPDATE SET
     mngr_enpswd = coalesce(excluded.mngr_enpswd, kids_own.TB_PP_M_EMP.mngr_enpswd)
    ,tmpr_pswd_yn = coalesce(excluded.tmpr_pswd_yn, kids_own.TB_PP_M_EMP.tmpr_pswd_yn)
    ,pswd_err_nmtm = coalesce(excluded.pswd_err_nmtm, kids_own.TB_PP_M_EMP.pswd_err_nmtm)
    ,encpt_bfr_pswd = coalesce(excluded.encpt_bfr_pswd, kids_own.TB_PP_M_EMP.encpt_bfr_pswd)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_EMP.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_관리자정보기본 삭제 (EmpMapper.deleteEmp) */
DELETE FROM kids_own.TB_PP_M_EMP
WHERE 1=1
AND emp_no = #{empNo}
