mapper.insert:
/* 대국민포털_관리자정보기본 입력 (EmpMapper.insertEmp) */
INSERT INTO "KIDS_OWN"."TB_PP_M_EMP"
(
     emp_no
    ,mngr_enpswd
    ,tmpr_pswd_yn
    ,pswd_err_nmtm
    ,bfr_enpswd
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{empNo}
    ,#{mngrEnpswd}
    ,#{tmprPswdYn}
    ,#{pswdErrNmtm}
    ,#{bfrEnpswd}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_관리자정보기본 저장 (EmpMapper.insertEmp) */
INSERT INTO "KIDS_OWN"."TB_PP_M_EMP"
(
     emp_no
    ,mngr_enpswd
    ,tmpr_pswd_yn
    ,pswd_err_nmtm
    ,bfr_enpswd
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{empNo}
    ,#{mngrEnpswd}
    ,#{tmprPswdYn}
    ,#{pswdErrNmtm}
    ,#{bfrEnpswd}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(emp_no)
DO UPDATE SET
     mngr_enpswd = coalesce(excluded.mngr_enpswd, "KIDS_OWN"."TB_PP_M_EMP".mngr_enpswd)
    ,tmpr_pswd_yn = coalesce(excluded.tmpr_pswd_yn, "KIDS_OWN"."TB_PP_M_EMP".tmpr_pswd_yn)
    ,pswd_err_nmtm = coalesce(excluded.pswd_err_nmtm, "KIDS_OWN"."TB_PP_M_EMP".pswd_err_nmtm)
    ,bfr_enpswd = coalesce(excluded.bfr_enpswd, "KIDS_OWN"."TB_PP_M_EMP".bfr_enpswd)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_EMP".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_EMP".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_관리자정보기본 삭제 (EmpMapper.deleteEmp) */
DELETE FROM "KIDS_OWN"."TB_PP_M_EMP"
WHERE 1=1
AND emp_no = #{empNo}
