mapper.select:
/* 대국민포털_전문가정보기본 조회 (ExprtInfoMapper.getExprtInfo) */ SELECT mbr_no ,brno ,exprt_ogdp_inst_encpt_eml ,exprt_hdof_yn ,exprt_aprv_stts_yn ,aprv_prcs_ymd ,rjct_rsn ,atch_file_id ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_EXPRT_INFO WHERE 1=1 AND mbr_no = #{mbrNo}

mapper.insert:
/* 대국민포털_전문가정보기본 입력 (ExprtInfoMapper.insertExprtInfo) */
INSERT INTO kids_own.TB_PP_M_EXPRT_INFO
(
     mbr_no
    ,brno
    ,exprt_ogdp_inst_encpt_eml
    ,exprt_hdof_yn
    ,exprt_aprv_stts_yn
    ,aprv_prcs_ymd
    ,rjct_rsn
    ,atch_file_id
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{mbrNo}
    ,#{brno}
    ,#{exprtOgdpInstEncptEml}
    ,#{exprtHdofYn}
    ,#{exprtAprvSttsYn}
    ,#{aprvPrcsYmd}
    ,#{rjctRsn}
    ,#{atchFileId}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_전문가정보기본 저장 (ExprtInfoMapper.insertExprtInfo) */
INSERT INTO kids_own.TB_PP_M_EXPRT_INFO
(
     mbr_no
    ,brno
    ,exprt_ogdp_inst_encpt_eml
    ,exprt_hdof_yn
    ,exprt_aprv_stts_yn
    ,aprv_prcs_ymd
    ,rjct_rsn
    ,atch_file_id
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{mbrNo}
    ,#{brno}
    ,#{exprtOgdpInstEncptEml}
    ,#{exprtHdofYn}
    ,#{exprtAprvSttsYn}
    ,#{aprvPrcsYmd}
    ,#{rjctRsn}
    ,#{atchFileId}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(mbr_no)
DO UPDATE SET
     brno = coalesce(excluded.brno, kids_own.TB_PP_M_EXPRT_INFO.brno)
    ,exprt_ogdp_inst_encpt_eml = coalesce(excluded.exprt_ogdp_inst_encpt_eml, kids_own.TB_PP_M_EXPRT_INFO.exprt_ogdp_inst_encpt_eml)
    ,exprt_hdof_yn = coalesce(excluded.exprt_hdof_yn, kids_own.TB_PP_M_EXPRT_INFO.exprt_hdof_yn)
    ,exprt_aprv_stts_yn = coalesce(excluded.exprt_aprv_stts_yn, kids_own.TB_PP_M_EXPRT_INFO.exprt_aprv_stts_yn)
    ,aprv_prcs_ymd = coalesce(excluded.aprv_prcs_ymd, kids_own.TB_PP_M_EXPRT_INFO.aprv_prcs_ymd)
    ,rjct_rsn = coalesce(excluded.rjct_rsn, kids_own.TB_PP_M_EXPRT_INFO.rjct_rsn)
    ,atch_file_id = coalesce(excluded.atch_file_id, kids_own.TB_PP_M_EXPRT_INFO.atch_file_id)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_EXPRT_INFO.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_전문가정보기본 삭제 (ExprtInfoMapper.deleteExprtInfo) */
DELETE FROM kids_own.TB_PP_M_EXPRT_INFO
WHERE 1=1
AND mbr_no = #{mbrNo}
