mapper.insert:
/* 대국민포털_전문가업무기본 입력 (ExprtTaskMapper.insertExprtTask) */
INSERT INTO "KIDS_OWN"."TB_PP_M_EXPRT_TASK"
(
     exprt_task_sn
    ,mbr_no
    ,task_se_cd
    ,brno
    ,exprt_aprv_stts_yn
    ,aprv_prcs_ymd
    ,rjct_rsn
    ,wrtr_dept_nm
    ,mdfr_dept_nm
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{exprtTaskSn}
    ,#{mbrNo}
    ,#{taskSeCd}
    ,#{brno}
    ,#{exprtAprvSttsYn}
    ,#{aprvPrcsYmd}
    ,#{rjctRsn}
    ,#{wrtrDeptNm}
    ,#{mdfrDeptNm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_전문가업무기본 저장 (ExprtTaskMapper.insertExprtTask) */
INSERT INTO "KIDS_OWN"."TB_PP_M_EXPRT_TASK"
(
     exprt_task_sn
    ,mbr_no
    ,task_se_cd
    ,brno
    ,exprt_aprv_stts_yn
    ,aprv_prcs_ymd
    ,rjct_rsn
    ,wrtr_dept_nm
    ,mdfr_dept_nm
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{exprtTaskSn}
    ,#{mbrNo}
    ,#{taskSeCd}
    ,#{brno}
    ,#{exprtAprvSttsYn}
    ,#{aprvPrcsYmd}
    ,#{rjctRsn}
    ,#{wrtrDeptNm}
    ,#{mdfrDeptNm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(exprt_task_sn)
DO UPDATE SET
     mbr_no = coalesce(excluded.mbr_no, "KIDS_OWN"."TB_PP_M_EXPRT_TASK".mbr_no)
    ,task_se_cd = coalesce(excluded.task_se_cd, "KIDS_OWN"."TB_PP_M_EXPRT_TASK".task_se_cd)
    ,brno = coalesce(excluded.brno, "KIDS_OWN"."TB_PP_M_EXPRT_TASK".brno)
    ,exprt_aprv_stts_yn = coalesce(excluded.exprt_aprv_stts_yn, "KIDS_OWN"."TB_PP_M_EXPRT_TASK".exprt_aprv_stts_yn)
    ,aprv_prcs_ymd = coalesce(excluded.aprv_prcs_ymd, "KIDS_OWN"."TB_PP_M_EXPRT_TASK".aprv_prcs_ymd)
    ,rjct_rsn = coalesce(excluded.rjct_rsn, "KIDS_OWN"."TB_PP_M_EXPRT_TASK".rjct_rsn)
    ,wrtr_dept_nm = coalesce(excluded.wrtr_dept_nm, "KIDS_OWN"."TB_PP_M_EXPRT_TASK".wrtr_dept_nm)
    ,mdfr_dept_nm = coalesce(excluded.mdfr_dept_nm, "KIDS_OWN"."TB_PP_M_EXPRT_TASK".mdfr_dept_nm)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_EXPRT_TASK".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_EXPRT_TASK".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_전문가업무기본 삭제 (ExprtTaskMapper.deleteExprtTask) */
DELETE FROM "KIDS_OWN"."TB_PP_M_EXPRT_TASK"
WHERE 1=1
AND exprt_task_sn = #{exprtTaskSn}
