mapper.insert:
/* 대국민포털_양식기본 입력 (FormMapper.insertForm) */
INSERT INTO "KIDS_OWN"."TB_PP_M_FORM"
(
     form_sn
    ,task_cd
    ,form_nm
    ,form_path
    ,use_yn
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{formSn}
    ,#{taskCd}
    ,#{formNm}
    ,#{formPath}
    ,#{useYn}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_양식기본 저장 (FormMapper.insertForm) */
INSERT INTO "KIDS_OWN"."TB_PP_M_FORM"
(
     form_sn
    ,task_cd
    ,form_nm
    ,form_path
    ,use_yn
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{formSn}
    ,#{taskCd}
    ,#{formNm}
    ,#{formPath}
    ,#{useYn}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(form_sn)
DO UPDATE SET
     task_cd = coalesce(excluded.task_cd, "KIDS_OWN"."TB_PP_M_FORM".task_cd)
    ,form_nm = coalesce(excluded.form_nm, "KIDS_OWN"."TB_PP_M_FORM".form_nm)
    ,form_path = coalesce(excluded.form_path, "KIDS_OWN"."TB_PP_M_FORM".form_path)
    ,use_yn = coalesce(excluded.use_yn, "KIDS_OWN"."TB_PP_M_FORM".use_yn)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_FORM".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_FORM".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_양식기본 삭제 (FormMapper.deleteForm) */
DELETE FROM "KIDS_OWN"."TB_PP_M_FORM"
WHERE 1=1
AND form_sn = #{formSn}
