mapper.select:
/* 대국민포털_양식기본 조회 (FormMapper.getForm) */ SELECT form_sn ,task_cd ,form_nm ,form_path ,use_yn ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_FORM WHERE 1=1 AND form_sn = #{formSn}

mapper.insert:
/* 대국민포털_양식기본 입력 (FormMapper.insertForm) */
INSERT INTO kids_own.TB_PP_M_FORM
(
     form_sn
    ,task_cd
    ,form_nm
    ,form_path
    ,use_yn
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{formSn}
    ,#{taskCd}
    ,#{formNm}
    ,#{formPath}
    ,#{useYn}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_양식기본 저장 (FormMapper.insertForm) */
INSERT INTO kids_own.TB_PP_M_FORM
(
     form_sn
    ,task_cd
    ,form_nm
    ,form_path
    ,use_yn
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{formSn}
    ,#{taskCd}
    ,#{formNm}
    ,#{formPath}
    ,#{useYn}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(form_sn)
DO UPDATE SET
     task_cd = coalesce(excluded.task_cd, kids_own.TB_PP_M_FORM.task_cd)
    ,form_nm = coalesce(excluded.form_nm, kids_own.TB_PP_M_FORM.form_nm)
    ,form_path = coalesce(excluded.form_path, kids_own.TB_PP_M_FORM.form_path)
    ,use_yn = coalesce(excluded.use_yn, kids_own.TB_PP_M_FORM.use_yn)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_FORM.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_양식기본 삭제 (FormMapper.deleteForm) */
DELETE FROM kids_own.TB_PP_M_FORM
WHERE 1=1
AND form_sn = #{formSn}
