mapper.insert:
/* 대국민포털_기관정보기본 입력 (InstMapper.insertInst) */
INSERT INTO "KIDS_OWN"."TB_PP_M_INST"
(
     brno
    ,inst_nm
    ,del_yn
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{brno}
    ,#{instNm}
    ,#{delYn}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_기관정보기본 저장 (InstMapper.insertInst) */
INSERT INTO "KIDS_OWN"."TB_PP_M_INST"
(
     brno
    ,inst_nm
    ,del_yn
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{brno}
    ,#{instNm}
    ,#{delYn}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(brno)
DO UPDATE SET
     inst_nm = coalesce(excluded.inst_nm, "KIDS_OWN"."TB_PP_M_INST".inst_nm)
    ,del_yn = coalesce(excluded.del_yn, "KIDS_OWN"."TB_PP_M_INST".del_yn)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_INST".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_INST".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_기관정보기본 삭제 (InstMapper.deleteInst) */
DELETE FROM "KIDS_OWN"."TB_PP_M_INST"
WHERE 1=1
AND brno = #{brno}
