mapper.select:
/* 대국민포털_기관정보기본 조회 (InstMapper.getInst) */ SELECT brno ,inst_nm ,del_yn ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_INST WHERE 1=1 AND brno = #{brno}

mapper.insert:
/* 대국민포털_기관정보기본 입력 (InstMapper.insertInst) */
INSERT INTO kids_own.TB_PP_M_INST
(
     brno
    ,inst_nm
    ,del_yn
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{brno}
    ,#{instNm}
    ,#{delYn}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_기관정보기본 저장 (InstMapper.insertInst) */ INSERT INTO kids_own.TB_PP_M_INST ( brno ,inst_nm ,del_yn ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
) VALUES ( #{brno} ,#{instNm} ,#{delYn} ,#{rgtrId} ,now()
,#{mdfrId} ,now()
) ON CONFLICT(brno) DO UPDATE SET inst_nm = coalesce(excluded.inst_nm, kids_own.TB_PP_M_INST.inst_nm) ,del_yn = coalesce(excluded.del_yn, kids_own.TB_PP_M_INST.del_yn) ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_INST.mdfr_id) ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_기관정보기본 삭제 (InstMapper.deleteInst) */
DELETE FROM kids_own.TB_PP_M_INST
WHERE 1=1
AND brno = #{brno}
