mapper.select:
/* 대국민포털_기관업무기본 조회 (InstTaskMapper.getInstTask) */ SELECT brno ,task_se_cd ,mbr_id ,use_yn ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_INST_TASK WHERE 1=1 AND brno = #{brno} AND task_se_cd = #{taskSeCd}

mapper.insert:
/* 대국민포털_기관업무기본 입력 (InstTaskMapper.insertInstTask) */
INSERT INTO kids_own.TB_PP_M_INST_TASK
(
     brno
    ,task_se_cd
    ,mbr_id
    ,use_yn
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{brno}
    ,#{taskSeCd}
    ,#{mbrId}
    ,#{useYn}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

mapper.update:
/* 대국민포털_기관업무기본 수정 (InstTaskMapper.updateInstTask) */
UPDATE kids_own.TB_PP_M_INST_TASK
SET
     mbr_id = coalesce(#{mbrId}, mbr_id)
    ,use_yn = coalesce(#{useYn}, use_yn)
    ,mdfr_id = coalesce(#{mdfrId}, mdfr_id)
    ,mdfcn_dt = now()

WHERE 1=1
AND brno = #{brno}
AND task_se_cd = #{taskSeCd}

mapper.insert:
/* 대국민포털_기관업무기본 저장 (InstTaskMapper.insertInstTask) */
INSERT INTO kids_own.TB_PP_M_INST_TASK
(
     brno
    ,task_se_cd
    ,mbr_id
    ,use_yn
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{brno}
    ,#{taskSeCd}
    ,#{mbrId}
    ,#{useYn}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(brno,task_se_cd)
DO UPDATE SET
     mbr_id = coalesce(excluded.mbr_id, kids_own.TB_PP_M_INST_TASK.mbr_id)
    ,use_yn = coalesce(excluded.use_yn, kids_own.TB_PP_M_INST_TASK.use_yn)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_INST_TASK.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_기관업무기본 삭제 (InstTaskMapper.deleteInstTask) */
DELETE FROM kids_own.TB_PP_M_INST_TASK
WHERE 1=1
AND brno = #{brno}
AND task_se_cd = #{taskSeCd}
