mapper.insert:
/* 대국민포털_기관업무기본 입력 (InstTaskMapper.insertInstTask) */
INSERT INTO "KIDS_OWN"."TB_PP_M_INST_TASK"
(
     brno
    ,task_se_cd
    ,mbr_id
    ,use_yn
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{brno}
    ,#{taskSeCd}
    ,#{mbrId}
    ,#{useYn}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_기관업무기본 저장 (InstTaskMapper.insertInstTask) */
INSERT INTO "KIDS_OWN"."TB_PP_M_INST_TASK"
(
     brno
    ,task_se_cd
    ,mbr_id
    ,use_yn
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{brno}
    ,#{taskSeCd}
    ,#{mbrId}
    ,#{useYn}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(brno,task_se_cd)
DO UPDATE SET
     mbr_id = coalesce(excluded.mbr_id, "KIDS_OWN"."TB_PP_M_INST_TASK".mbr_id)
    ,use_yn = coalesce(excluded.use_yn, "KIDS_OWN"."TB_PP_M_INST_TASK".use_yn)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_INST_TASK".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_INST_TASK".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_기관업무기본 삭제 (InstTaskMapper.deleteInstTask) */
DELETE FROM "KIDS_OWN"."TB_PP_M_INST_TASK"
WHERE 1=1
AND brno = #{brno}
AND task_se_cd = #{taskSeCd}
