mapper.select:
/* 대국민포털_회원정보기본 기존 아이디, 패스워드 기준으로 데이터 존재 여부 조회 (MbrInfoMapper.verifyPassword) */
SELECT COUNT(*) as cnt
FROM kids_own.TB_PP_M_MBR_INFO
WHERE 1=1
AND mbr_id = #{mbrId}
AND mbr_enpswd = #{mbrEnpswd}
AND mbr_whdwl_dt is null

/* 대국민포털_회원정보기본 기존 아이디, 이메일 존재여부 조회 (MbrInfoMapper.checkMbrInfo) */
SELECT COUNT(*) as cnt
FROM kids_own.TB_PP_M_MBR_INFO
WHERE 1=1

    AND mbr_id = #{mbrId}


    AND mbr_encpt_eml = #{mbrEncptEml}

AND mbr_whdwl_dt is null

/* 대국민포털_회원정보기본 조회 (MbrInfoMapper.getMbrInfo) */ SELECT mbr_no ,mbr_id ,mbr_encpt_flnm ,mbr_encpt_eml ,mbr_enpswd ,mbr_encpt_telno ,mbr_type_cd ,mbr_join_stts ,mbr_join_dt ,mbr_whdwl_rsn ,mbr_whdwl_dt ,bfr_enpswd ,pswd_chg_dt ,pswd_err_nmtm ,link_info_idntf_id ,cert_token ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_MBR_INFO WHERE 1=1
AND mbr_no = #{mbrNo}
AND mbr_id = #{mbrId}
AND mbr_whdwl_dt is null

/* 대국민포털_회원정보기본 입력 (MbrInfoMapper.insertMbrInfo) */
WITH seq AS (
  INSERT INTO kids_own.TB_PP_M_MBR_NO_SEQ (yyyy, last_seq)
  VALUES (to_char(CURRENT_TIMESTAMP, 'YYYY'), 1)
  ON CONFLICT (yyyy)
  DO UPDATE SET last_seq = "TB_PP_M_MBR_NO_SEQ".last_seq + 1
  RETURNING yyyy, last_seq
),
ins AS (
  INSERT INTO kids_own.TB_PP_M_MBR_INFO
  (
       mbr_no
      ,mbr_id
      ,mbr_encpt_flnm
      ,mbr_encpt_eml
      ,mbr_enpswd
      ,mbr_encpt_telno
      ,mbr_type_cd
      ,mbr_join_stts
      ,mbr_join_dt
      ,mbr_whdwl_rsn
      ,mbr_whdwl_dt
      ,bfr_enpswd
      ,pswd_chg_dt
      ,pswd_err_nmtm
      ,link_info_idntf_id
      ,cert_token
      ,rgtr_id
      ,reg_dt

      ,mdfr_id
      ,mdfcn_dt

  )
  SELECT
     seq.yyyy || LPAD(seq.last_seq::text, 6, '0') AS mbr_no
    ,#{mbrId}
    ,#{mbrEncptFlnm}
    ,#{mbrEncptEml}
    ,#{mbrEnpswd}
    ,#{mbrEncptTelno}
    ,#{mbrTypeCd}
    ,#{mbrJoinStts}
    ,to_timestamp(#{mbrJoinDt}, 'YYYY-MM-DD HH24:MI:SS')
    ,#{mbrWhdwlRsn}
    ,#{mbrWhdwlDt}
    ,#{bfrEnpswd}
    ,#{pswdChgDt}
    ,#{pswdErrNmtm}
    ,#{linkInfoIdntfId}
    ,#{certToken}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

  FROM seq
  RETURNING mbr_no
)
SELECT mbr_no FROM ins

mapper.update:
/* 대국민포털_회원정보기본 수정 (MbrInfoMapper.updateMbrInfo) */ UPDATE kids_own.TB_PP_M_MBR_INFO SET mbr_id = coalesce(#{mbrId}, mbr_id) ,mbr_encpt_flnm = coalesce(#{mbrEncptFlnm}, mbr_encpt_flnm) ,mbr_encpt_eml = coalesce(#{mbrEncptEml}, mbr_encpt_eml) ,mbr_enpswd = coalesce(#{mbrEnpswd}, mbr_enpswd) ,mbr_encpt_telno = coalesce(#{mbrEncptTelno}, mbr_encpt_telno) ,mbr_type_cd = coalesce(#{mbrTypeCd}, mbr_type_cd) ,mbr_join_stts = coalesce(#{mbrJoinStts}, mbr_join_stts) ,mbr_join_dt = coalesce(to_timestamp(#{mbrJoinDt}, 'YYYY-MM-DD HH24:MI:SS'), mbr_join_dt) ,mbr_whdwl_rsn = coalesce(#{mbrWhdwlRsn}, mbr_whdwl_rsn) ,mbr_whdwl_dt = coalesce(to_timestamp(#{mbrWhdwlDt}, 'YYYY-MM-DD HH24:MI:SS'), mbr_whdwl_dt) ,bfr_enpswd = coalesce(#{bfrEnpswd}, bfr_enpswd) ,pswd_chg_dt = coalesce(to_timestamp(#{pswdChgDt}, 'YYYY-MM-DD HH24:MI:SS'), pswd_chg_dt) ,pswd_err_nmtm = coalesce(#{pswdErrNmtm}, pswd_err_nmtm) ,link_info_idntf_id = coalesce(#{linkInfoIdntfId}, link_info_idntf_id) ,cert_token = coalesce(#{certToken}, cert_token) ,mdfr_id = coalesce(#{mdfrId}, mdfr_id) ,mdfcn_dt = now()
WHERE 1=1
AND mbr_no = #{mbrNo}
AND mbr_id = #{mbrId}

mapper.insert:
/* 대국민포털_회원정보기본 저장 (MbrInfoMapper.insertMbrInfo) */
INSERT INTO kids_own.TB_PP_M_MBR_INFO
(
     mbr_no
    ,mbr_id
    ,mbr_encpt_flnm
    ,mbr_encpt_eml
    ,mbr_enpswd
    ,mbr_encpt_telno
    ,mbr_type_cd
    ,mbr_join_stts
    ,mbr_join_dt
    ,mbr_whdwl_rsn
    ,mbr_whdwl_dt
    ,bfr_enpswd
    ,pswd_chg_dt
    ,pswd_err_nmtm
    ,link_info_idntf_id
    ,cert_token
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{mbrNo}
    ,#{mbrId}
    ,#{mbrEncptFlnm}
    ,#{mbrEncptEml}
    ,#{mbrEnpswd}
    ,#{mbrEncptTelno}
    ,#{mbrTypeCd}
    ,#{mbrJoinStts}
    ,#{mbrJoinDt}
    ,#{mbrWhdwlRsn}
    ,#{mbrWhdwlDt}
    ,#{bfrEnpswd}
    ,#{pswdChgDt}
    ,#{pswdErrNmtm}
    ,#{linkInfoIdntfId}
    ,#{certToken}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(mbr_no)
DO UPDATE SET
     mbr_id = coalesce(excluded.mbr_id, kids_own.TB_PP_M_MBR_INFO.mbr_id)
    ,mbr_encpt_flnm = coalesce(excluded.mbr_encpt_flnm, kids_own.TB_PP_M_MBR_INFO.mbr_encpt_flnm)
    ,mbr_encpt_eml = coalesce(excluded.mbr_encpt_eml, kids_own.TB_PP_M_MBR_INFO.mbr_encpt_eml)
    ,mbr_enpswd = coalesce(excluded.mbr_enpswd, kids_own.TB_PP_M_MBR_INFO.mbr_enpswd)
    ,mbr_encpt_telno = coalesce(excluded.mbr_encpt_telno, kids_own.TB_PP_M_MBR_INFO.mbr_encpt_telno)
    ,mbr_type_cd = coalesce(excluded.mbr_type_cd, kids_own.TB_PP_M_MBR_INFO.mbr_type_cd)
    ,mbr_join_stts = coalesce(excluded.mbr_join_stts, kids_own.TB_PP_M_MBR_INFO.mbr_join_stts)
    ,mbr_join_dt = coalesce(excluded.mbr_join_dt, kids_own.TB_PP_M_MBR_INFO.mbr_join_dt)
    ,mbr_whdwl_rsn = coalesce(excluded.mbr_whdwl_rsn, kids_own.TB_PP_M_MBR_INFO.mbr_whdwl_rsn)
    ,mbr_whdwl_dt = coalesce(excluded.mbr_whdwl_dt, kids_own.TB_PP_M_MBR_INFO.mbr_whdwl_dt)
    ,bfr_enpswd = coalesce(excluded.bfr_enpswd, kids_own.TB_PP_M_MBR_INFO.bfr_enpswd)
    ,pswd_chg_dt = coalesce(excluded.pswd_chg_dt, kids_own.TB_PP_M_MBR_INFO.pswd_chg_dt)
    ,pswd_err_nmtm = coalesce(excluded.pswd_err_nmtm, kids_own.TB_PP_M_MBR_INFO.pswd_err_nmtm)
    ,link_info_idntf_id = coalesce(excluded.link_info_idntf_id, kids_own.TB_PP_M_MBR_INFO.link_info_idntf_id)
    ,cert_token = coalesce(excluded.cert_token, kids_own.TB_PP_M_MBR_INFO.cert_token)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_MBR_INFO.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_회원정보기본 삭제 (MbrInfoMapper.deleteMbrInfo) */
DELETE FROM kids_own.TB_PP_M_MBR_INFO
WHERE 1=1
AND mbr_no = #{mbrNo}
