mapper.select:
/* 대국민포털_법정대리인정보기본 조회 (SttyAgtInfoMapper.getSttyAgtInfo) */ SELECT mbr_no ,stty_agt_nm ,stty_agt_encpt_telno ,stty_agt_rel ,link_info_idntf_id ,cert_token_vl ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_STTY_AGT_INFO WHERE 1=1 AND mbr_no = #{mbrNo}

mapper.insert:
/* 대국민포털_법정대리인정보기본 입력 (SttyAgtInfoMapper.insertSttyAgtInfo) */
INSERT INTO kids_own.TB_PP_M_STTY_AGT_INFO
(
     mbr_no
    ,stty_agt_nm
    ,stty_agt_encpt_telno
    ,stty_agt_rel
    ,link_info_idntf_id
    ,cert_token_vl
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{mbrNo}
    ,#{sttyAgtNm}
    ,#{sttyAgtEncptTelno}
    ,#{sttyAgtRel}
    ,#{linkInfoIdntfId}
    ,#{certTokenVl}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_법정대리인정보기본 저장 (SttyAgtInfoMapper.insertSttyAgtInfo) */
INSERT INTO kids_own.TB_PP_M_STTY_AGT_INFO
(
     mbr_no
    ,stty_agt_nm
    ,stty_agt_encpt_telno
    ,stty_agt_rel
    ,link_info_idntf_id
    ,cert_token_vl
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{mbrNo}
    ,#{sttyAgtNm}
    ,#{sttyAgtEncptTelno}
    ,#{sttyAgtRel}
    ,#{linkInfoIdntfId}
    ,#{certTokenVl}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(mbr_no)
DO UPDATE SET
     stty_agt_nm = coalesce(excluded.stty_agt_nm, kids_own.TB_PP_M_STTY_AGT_INFO.stty_agt_nm)
    ,stty_agt_encpt_telno = coalesce(excluded.stty_agt_encpt_telno, kids_own.TB_PP_M_STTY_AGT_INFO.stty_agt_encpt_telno)
    ,stty_agt_rel = coalesce(excluded.stty_agt_rel, kids_own.TB_PP_M_STTY_AGT_INFO.stty_agt_rel)
    ,link_info_idntf_id = coalesce(excluded.link_info_idntf_id, kids_own.TB_PP_M_STTY_AGT_INFO.link_info_idntf_id)
    ,cert_token_vl = coalesce(excluded.cert_token_vl, kids_own.TB_PP_M_STTY_AGT_INFO.cert_token_vl)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_STTY_AGT_INFO.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_법정대리인정보기본 삭제 (SttyAgtInfoMapper.deleteSttyAgtInfo) */
DELETE FROM kids_own.TB_PP_M_STTY_AGT_INFO
WHERE 1=1
AND mbr_no = #{mbrNo}
