mapper.select:
/* 대국민포털_댓글기본 조회 (CmntMapper.getCmnt) */ SELECT cmnt_sn ,pst_sn ,cmnt_cn ,cmnt_enpswd ,use_yn ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_CMNT WHERE 1=1 AND cmnt_sn = #{cmntSn}

mapper.insert:
/* 대국민포털_댓글기본 입력 (CmntMapper.insertCmnt) */
INSERT INTO kids_own.TB_PP_M_CMNT
(
     cmnt_sn
    ,pst_sn
    ,cmnt_cn
    ,cmnt_enpswd
    ,use_yn
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{cmntSn}
    ,#{pstSn}
    ,#{cmntCn}
    ,#{cmntEnpswd}
    ,#{useYn}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_댓글기본 저장 (CmntMapper.insertCmnt) */
INSERT INTO kids_own.TB_PP_M_CMNT
(
     cmnt_sn
    ,pst_sn
    ,cmnt_cn
    ,cmnt_enpswd
    ,use_yn
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{cmntSn}
    ,#{pstSn}
    ,#{cmntCn}
    ,#{cmntEnpswd}
    ,#{useYn}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(cmnt_sn)
DO UPDATE SET
     pst_sn = coalesce(excluded.pst_sn, kids_own.TB_PP_M_CMNT.pst_sn)
    ,cmnt_cn = coalesce(excluded.cmnt_cn, kids_own.TB_PP_M_CMNT.cmnt_cn)
    ,cmnt_enpswd = coalesce(excluded.cmnt_enpswd, kids_own.TB_PP_M_CMNT.cmnt_enpswd)
    ,use_yn = coalesce(excluded.use_yn, kids_own.TB_PP_M_CMNT.use_yn)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_CMNT.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_댓글기본 삭제 (CmntMapper.deleteCmnt) */
DELETE FROM kids_own.TB_PP_M_CMNT
WHERE 1=1
AND cmnt_sn = #{cmntSn}
