mapper.insert:
/* 대국민포털_댓글기본 입력 (CmntMapper.insertCmnt) */
INSERT INTO "KIDS_OWN"."TB_PP_M_CMNT"
(
     cmnt_sn
    ,pst_sn
    ,cmnt_cn
    ,cmnt_enpswd
    ,use_yn
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{cmntSn}
    ,#{pstSn}
    ,#{cmntCn}
    ,#{cmntEnpswd}
    ,#{useYn}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_댓글기본 저장 (CmntMapper.insertCmnt) */
INSERT INTO "KIDS_OWN"."TB_PP_M_CMNT"
(
     cmnt_sn
    ,pst_sn
    ,cmnt_cn
    ,cmnt_enpswd
    ,use_yn
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{cmntSn}
    ,#{pstSn}
    ,#{cmntCn}
    ,#{cmntEnpswd}
    ,#{useYn}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(cmnt_sn)
DO UPDATE SET
     pst_sn = coalesce(excluded.pst_sn, "KIDS_OWN"."TB_PP_M_CMNT".pst_sn)
    ,cmnt_cn = coalesce(excluded.cmnt_cn, "KIDS_OWN"."TB_PP_M_CMNT".cmnt_cn)
    ,cmnt_enpswd = coalesce(excluded.cmnt_enpswd, "KIDS_OWN"."TB_PP_M_CMNT".cmnt_enpswd)
    ,use_yn = coalesce(excluded.use_yn, "KIDS_OWN"."TB_PP_M_CMNT".use_yn)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_CMNT".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_CMNT".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_댓글기본 삭제 (CmntMapper.deleteCmnt) */
DELETE FROM "KIDS_OWN"."TB_PP_M_CMNT"
WHERE 1=1
AND cmnt_sn = #{cmntSn}
