mapper.select:
/* 대국민포털_팝업기본 조회 (PopupMapper.getPopup) */ SELECT popup_sn ,popup_ttl ,popup_pstg_bgng_ymd ,popup_pstg_end_ymd ,popup_lnkg_addr ,popup_seq ,popup_pstg_yn ,popup_npag_yn ,popup_expln ,atch_file_id ,wrtr_dept_nm ,mdfr_dept_nm ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_POPUP WHERE 1=1 AND popup_sn = #{popupSn}

mapper.insert:
/* 대국민포털_팝업기본 입력 (PopupMapper.insertPopup) */
INSERT INTO kids_own.TB_PP_M_POPUP
(
     popup_sn
    ,popup_ttl
    ,popup_pstg_bgng_ymd
    ,popup_pstg_end_ymd
    ,popup_lnkg_addr
    ,popup_seq
    ,popup_pstg_yn
    ,popup_npag_yn
    ,popup_expln
    ,atch_file_id
    ,wrtr_dept_nm
    ,mdfr_dept_nm
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{popupSn}
    ,#{popupTtl}
    ,#{popupPstgBgngYmd}
    ,#{popupPstgEndYmd}
    ,#{popupLnkgAddr}
    ,#{popupSeq}
    ,#{popupPstgYn}
    ,#{popupNpagYn}
    ,#{popupExpln}
    ,#{atchFileId}
    ,#{wrtrDeptNm}
    ,#{mdfrDeptNm}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_팝업기본 저장 (PopupMapper.insertPopup) */
INSERT INTO kids_own.TB_PP_M_POPUP
(
     popup_sn
    ,popup_ttl
    ,popup_pstg_bgng_ymd
    ,popup_pstg_end_ymd
    ,popup_lnkg_addr
    ,popup_seq
    ,popup_pstg_yn
    ,popup_npag_yn
    ,popup_expln
    ,atch_file_id
    ,wrtr_dept_nm
    ,mdfr_dept_nm
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{popupSn}
    ,#{popupTtl}
    ,#{popupPstgBgngYmd}
    ,#{popupPstgEndYmd}
    ,#{popupLnkgAddr}
    ,#{popupSeq}
    ,#{popupPstgYn}
    ,#{popupNpagYn}
    ,#{popupExpln}
    ,#{atchFileId}
    ,#{wrtrDeptNm}
    ,#{mdfrDeptNm}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(popup_sn)
DO UPDATE SET
     popup_ttl = coalesce(excluded.popup_ttl, kids_own.TB_PP_M_POPUP.popup_ttl)
    ,popup_pstg_bgng_ymd = coalesce(excluded.popup_pstg_bgng_ymd, kids_own.TB_PP_M_POPUP.popup_pstg_bgng_ymd)
    ,popup_pstg_end_ymd = coalesce(excluded.popup_pstg_end_ymd, kids_own.TB_PP_M_POPUP.popup_pstg_end_ymd)
    ,popup_lnkg_addr = coalesce(excluded.popup_lnkg_addr, kids_own.TB_PP_M_POPUP.popup_lnkg_addr)
    ,popup_seq = coalesce(excluded.popup_seq, kids_own.TB_PP_M_POPUP.popup_seq)
    ,popup_pstg_yn = coalesce(excluded.popup_pstg_yn, kids_own.TB_PP_M_POPUP.popup_pstg_yn)
    ,popup_npag_yn = coalesce(excluded.popup_npag_yn, kids_own.TB_PP_M_POPUP.popup_npag_yn)
    ,popup_expln = coalesce(excluded.popup_expln, kids_own.TB_PP_M_POPUP.popup_expln)
    ,atch_file_id = coalesce(excluded.atch_file_id, kids_own.TB_PP_M_POPUP.atch_file_id)
    ,wrtr_dept_nm = coalesce(excluded.wrtr_dept_nm, kids_own.TB_PP_M_POPUP.wrtr_dept_nm)
    ,mdfr_dept_nm = coalesce(excluded.mdfr_dept_nm, kids_own.TB_PP_M_POPUP.mdfr_dept_nm)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_POPUP.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_팝업기본 삭제 (PopupMapper.deletePopup) */
DELETE FROM kids_own.TB_PP_M_POPUP
WHERE 1=1
AND popup_sn = #{popupSn}
