mapper.insert:
/* 대국민포털_약관법령기본 입력 (TrmsSttMapper.insertTrmsStt) */
INSERT INTO "KIDS_OWN"."TB_PP_M_TRMS_STT"
(
     trms_stt_sn
    ,trms_stt_cd
    ,trms_stt_aplcn_day
    ,trms_stt_end_day
    ,trms_stt_cn
    ,atch_file_id
    ,wrtr_dept_nm
    ,mdfr_dept_nm
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{trmsSttSn}
    ,#{trmsSttCd}
    ,#{trmsSttAplcnDay}
    ,#{trmsSttEndDay}
    ,#{trmsSttCn}
    ,#{atchFileId}
    ,#{wrtrDeptNm}
    ,#{mdfrDeptNm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_약관법령기본 저장 (TrmsSttMapper.insertTrmsStt) */
INSERT INTO "KIDS_OWN"."TB_PP_M_TRMS_STT"
(
     trms_stt_sn
    ,trms_stt_cd
    ,trms_stt_aplcn_day
    ,trms_stt_end_day
    ,trms_stt_cn
    ,atch_file_id
    ,wrtr_dept_nm
    ,mdfr_dept_nm
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{trmsSttSn}
    ,#{trmsSttCd}
    ,#{trmsSttAplcnDay}
    ,#{trmsSttEndDay}
    ,#{trmsSttCn}
    ,#{atchFileId}
    ,#{wrtrDeptNm}
    ,#{mdfrDeptNm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(trms_stt_sn)
DO UPDATE SET
     trms_stt_cd = coalesce(excluded.trms_stt_cd, "KIDS_OWN"."TB_PP_M_TRMS_STT".trms_stt_cd)
    ,trms_stt_aplcn_day = coalesce(excluded.trms_stt_aplcn_day, "KIDS_OWN"."TB_PP_M_TRMS_STT".trms_stt_aplcn_day)
    ,trms_stt_end_day = coalesce(excluded.trms_stt_end_day, "KIDS_OWN"."TB_PP_M_TRMS_STT".trms_stt_end_day)
    ,trms_stt_cn = coalesce(excluded.trms_stt_cn, "KIDS_OWN"."TB_PP_M_TRMS_STT".trms_stt_cn)
    ,atch_file_id = coalesce(excluded.atch_file_id, "KIDS_OWN"."TB_PP_M_TRMS_STT".atch_file_id)
    ,wrtr_dept_nm = coalesce(excluded.wrtr_dept_nm, "KIDS_OWN"."TB_PP_M_TRMS_STT".wrtr_dept_nm)
    ,mdfr_dept_nm = coalesce(excluded.mdfr_dept_nm, "KIDS_OWN"."TB_PP_M_TRMS_STT".mdfr_dept_nm)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_TRMS_STT".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_TRMS_STT".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_약관법령기본 삭제 (TrmsSttMapper.deleteTrmsStt) */
DELETE FROM "KIDS_OWN"."TB_PP_M_TRMS_STT"
WHERE 1=1
AND trms_stt_sn = #{trmsSttSn}
