mapper.insert:
/* 대국민포털_업무코드기본 입력 (TaskCdMapper.insertTaskCd) */
INSERT INTO "KIDS_OWN"."TB_PP_M_TASK_CD"
(
     task_cd
    ,task_cd_nm
    ,up_task_cd
    ,task_cd_vl
    ,task_cd_expln
    ,use_yn
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{taskCd}
    ,#{taskCdNm}
    ,#{upTaskCd}
    ,#{taskCdVl}
    ,#{taskCdExpln}
    ,#{useYn}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_업무코드기본 저장 (TaskCdMapper.insertTaskCd) */
INSERT INTO "KIDS_OWN"."TB_PP_M_TASK_CD"
(
     task_cd
    ,task_cd_nm
    ,up_task_cd
    ,task_cd_vl
    ,task_cd_expln
    ,use_yn
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{taskCd}
    ,#{taskCdNm}
    ,#{upTaskCd}
    ,#{taskCdVl}
    ,#{taskCdExpln}
    ,#{useYn}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(task_cd)
DO UPDATE SET
     task_cd_nm = coalesce(excluded.task_cd_nm, "KIDS_OWN"."TB_PP_M_TASK_CD".task_cd_nm)
    ,up_task_cd = coalesce(excluded.up_task_cd, "KIDS_OWN"."TB_PP_M_TASK_CD".up_task_cd)
    ,task_cd_vl = coalesce(excluded.task_cd_vl, "KIDS_OWN"."TB_PP_M_TASK_CD".task_cd_vl)
    ,task_cd_expln = coalesce(excluded.task_cd_expln, "KIDS_OWN"."TB_PP_M_TASK_CD".task_cd_expln)
    ,use_yn = coalesce(excluded.use_yn, "KIDS_OWN"."TB_PP_M_TASK_CD".use_yn)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_TASK_CD".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_TASK_CD".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_업무코드기본 삭제 (TaskCdMapper.deleteTaskCd) */
DELETE FROM "KIDS_OWN"."TB_PP_M_TASK_CD"
WHERE 1=1
AND task_cd = #{taskCd}
