mapper.insert:
/* 대국민포털_용어기본 입력 (TrmMapper.insertTrm) */
INSERT INTO "KIDS_OWN"."TB_PP_M_TRM"
(
     std_trm_nm
    ,sys_se_nm
    ,artcl_sou_nm
    ,std_trm_eng_abbr_nm
    ,atrb_type_nm
    ,com_std_dmn_nm
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{stdTrmNm}
    ,#{sysSeNm}
    ,#{artclSouNm}
    ,#{stdTrmEngAbbrNm}
    ,#{atrbTypeNm}
    ,#{comStdDmnNm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)

/* 대국민포털_용어기본 저장 (TrmMapper.insertTrm) */
INSERT INTO "KIDS_OWN"."TB_PP_M_TRM"
(
     std_trm_nm
    ,sys_se_nm
    ,artcl_sou_nm
    ,std_trm_eng_abbr_nm
    ,atrb_type_nm
    ,com_std_dmn_nm
    ,rgtr_id
    ,reg_dt
    ,reg_prgrm_id
    ,mdfr_id
    ,mdfcn_dt
    ,mdfcn_prgrm_id
)
VALUES
(
     #{stdTrmNm}
    ,#{sysSeNm}
    ,#{artclSouNm}
    ,#{stdTrmEngAbbrNm}
    ,#{atrbTypeNm}
    ,#{comStdDmnNm}
    ,#{rgtrId}
    ,now()
    ,#{regPrgrmId}
    ,#{mdfrId}
    ,now()
    ,#{mdfcnPrgrmId}
)
ON CONFLICT(std_trm_nm)
DO UPDATE SET
     sys_se_nm = coalesce(excluded.sys_se_nm, "KIDS_OWN"."TB_PP_M_TRM".sys_se_nm)
    ,artcl_sou_nm = coalesce(excluded.artcl_sou_nm, "KIDS_OWN"."TB_PP_M_TRM".artcl_sou_nm)
    ,std_trm_eng_abbr_nm = coalesce(excluded.std_trm_eng_abbr_nm, "KIDS_OWN"."TB_PP_M_TRM".std_trm_eng_abbr_nm)
    ,atrb_type_nm = coalesce(excluded.atrb_type_nm, "KIDS_OWN"."TB_PP_M_TRM".atrb_type_nm)
    ,com_std_dmn_nm = coalesce(excluded.com_std_dmn_nm, "KIDS_OWN"."TB_PP_M_TRM".com_std_dmn_nm)
    ,mdfr_id = coalesce(excluded.mdfr_id, "KIDS_OWN"."TB_PP_M_TRM".mdfr_id)
    ,mdfcn_dt = now()
    ,mdfcn_prgrm_id = coalesce(excluded.mdfcn_prgrm_id, "KIDS_OWN"."TB_PP_M_TRM".mdfcn_prgrm_id)

mapper.delete:
/* 대국민포털_용어기본 삭제 (TrmMapper.deleteTrm) */
DELETE FROM "KIDS_OWN"."TB_PP_M_TRM"
WHERE 1=1
AND std_trm_nm = #{stdTrmNm}
