mapper.select:
/* 대국민포털_단어기본 조회 (WordMapper.getWord) */ SELECT com_std_word_nm ,sys_se_nm ,artcl_sou_nm ,com_std_word_eng_abbr_nm ,com_std_word_eng_nm ,com_std_word_expln ,frm_word_yn ,rgtr_id ,reg_dt
,mdfr_id ,mdfcn_dt
FROM kids_own.TB_PP_M_WORD WHERE 1=1 AND com_std_word_nm = #{comStdWordNm}

mapper.insert:
/* 대국민포털_단어기본 입력 (WordMapper.insertWord) */
INSERT INTO kids_own.TB_PP_M_WORD
(
     com_std_word_nm
    ,sys_se_nm
    ,artcl_sou_nm
    ,com_std_word_eng_abbr_nm
    ,com_std_word_eng_nm
    ,com_std_word_expln
    ,frm_word_yn
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{comStdWordNm}
    ,#{sysSeNm}
    ,#{artclSouNm}
    ,#{comStdWordEngAbbrNm}
    ,#{comStdWordEngNm}
    ,#{comStdWordExpln}
    ,#{frmWordYn}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)

/* 대국민포털_단어기본 저장 (WordMapper.insertWord) */
INSERT INTO kids_own.TB_PP_M_WORD
(
     com_std_word_nm
    ,sys_se_nm
    ,artcl_sou_nm
    ,com_std_word_eng_abbr_nm
    ,com_std_word_eng_nm
    ,com_std_word_expln
    ,frm_word_yn
    ,rgtr_id
    ,reg_dt

    ,mdfr_id
    ,mdfcn_dt

)
VALUES
(
     #{comStdWordNm}
    ,#{sysSeNm}
    ,#{artclSouNm}
    ,#{comStdWordEngAbbrNm}
    ,#{comStdWordEngNm}
    ,#{comStdWordExpln}
    ,#{frmWordYn}
    ,#{rgtrId}
    ,now()

    ,#{mdfrId}
    ,now()

)
ON CONFLICT(com_std_word_nm)
DO UPDATE SET
     sys_se_nm = coalesce(excluded.sys_se_nm, kids_own.TB_PP_M_WORD.sys_se_nm)
    ,artcl_sou_nm = coalesce(excluded.artcl_sou_nm, kids_own.TB_PP_M_WORD.artcl_sou_nm)
    ,com_std_word_eng_abbr_nm = coalesce(excluded.com_std_word_eng_abbr_nm, kids_own.TB_PP_M_WORD.com_std_word_eng_abbr_nm)
    ,com_std_word_eng_nm = coalesce(excluded.com_std_word_eng_nm, kids_own.TB_PP_M_WORD.com_std_word_eng_nm)
    ,com_std_word_expln = coalesce(excluded.com_std_word_expln, kids_own.TB_PP_M_WORD.com_std_word_expln)
    ,frm_word_yn = coalesce(excluded.frm_word_yn, kids_own.TB_PP_M_WORD.frm_word_yn)
    ,mdfr_id = coalesce(excluded.mdfr_id, kids_own.TB_PP_M_WORD.mdfr_id)
    ,mdfcn_dt = now()

mapper.delete:
/* 대국민포털_단어기본 삭제 (WordMapper.deleteWord) */
DELETE FROM kids_own.TB_PP_M_WORD
WHERE 1=1
AND com_std_word_nm = #{comStdWordNm}
